Action.ExecScriptExpression:
using System.Windows.Forms;
using System.Threading;
using Triggernometry;

public bool IsCafeACT()
{
    Control current = RealPlugin.plug.ui;
    while (current != null && !(current is TabControl))
    {
        current = current.Parent;
    }

    if (current is TabControl tabControl)
    {
        foreach (TabPage tabPage in tabControl.TabPages)
        {
            if (tabPage.Text == "插件中心")
            {
                return true;
            }
        }
    }

    return false;
}

void Log(string msg) => Triggernometry.Interpreter.StaticHelpers.Log(RealPlugin.DebugLevelEnum.Warning, msg);
void Error(string msg) => Triggernometry.Interpreter.StaticHelpers.Log(RealPlugin.DebugLevelEnum.Error, msg);

if (IsCafeACT())
{
    Log("  不要截图到处问这是什么意思该怎么办，上面写得很清楚了。");
    Thread.Sleep(10);
    Log("  如果你想使用高级功能，目前已知的解决方法仅有更换呆萌整合版本。");
    Thread.Sleep(10);
    Log("  鲶鱼精邮差扩展无法运行，将只有基础的场地标点功能。");
    Thread.Sleep(10);
    Log("  由于 CafeACT 使用旧版 ACT 且未解决程序集版本冲突，");
    Thread.Sleep(10);
    Error("[高级标点工具箱] 检测到 CafeACT 环境。");
    Triggernometry.RealPlugin.plug.RegisterNamedCallback("hint", new Action<object, string>((_, __) => { }), null, registrant: "注册失败");
}

if (new Version("${_pluginversion}") < new Version("${env:trigversion}"))
{
    Error("[高级标点工具箱] 当前触发器版本 ${_pluginversion}，需要更新至 ${env:trigversion}");
}

using System;
using System.Collections.Generic;
using System.Windows.Forms;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using PostNamazu;
using PostNamazu.Actions;
using PostNamazu.Common;
using GreyMagic;
using Triggernometry;
using Triggernometry.PluginBridges;
using System.Numerics;
using System.Text.RegularExpressions;
using static Triggernometry.RealPlugin;
using static Triggernometry.Utilities.Memory;
using Triggernometry.Variables;

bool initialized = false;
int i = 1;
for (i = 1; i <= 5; i++)
{
    try
    {
        PostNamazuExtension.Init();
        initialized = true;
        break;
    }
    catch
    {
        Interpreter.StaticHelpers.Log(DebugLevelEnum.Warning, $"[鲶鱼精邮差扩展] 初始化失败，正在重试第 {i} / 5 次……");
        if (i == 5) throw;
    }
    Thread.Sleep(i * 1000);
}
PostNamazuExtension.Log(i > 1 ? DebugLevelEnum.Warning : DebugLevelEnum.Custom, $"[鲶鱼精邮差扩展] 初始化成功。");

public static class PostNamazuExtension
{

    #region Common

    private static readonly string Name = "PostNamazuExtension";

    public static PostNamazu.PostNamazu NamazuPlugin => (PostNamazu.PostNamazu)RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").pluginObj;
    public static ExternalProcessMemory Memory => NamazuPlugin?.Memory;
    public static SigScanner Scanner => NamazuPlugin?.SigScanner;
    private static PostNamazuUi _postNamazuUi;
    public static PostNamazuUi PostNamazuUi
    {
        get
        {
            if (_postNamazuUi?.Parent as TabPage == null)
            {
                _postNamazuUi = GetPostNamazuUi();
            }
            return _postNamazuUi;
        }
    }

    public static bool IsReady => Triggernometry.Interpreter.StaticHelpers.XivProcess != null && Memory != null;

    private static PostNamazuUi GetPostNamazuUi()
    {
        TabPage tp = RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").TabPage;
        return tp.Controls.OfType<PostNamazuUi>().FirstOrDefault();
    }

    public static void OnXivProcUpdate()
    {
        ScanSignatures();
        DisableAfkCountDown();
    }

    public static void RegisterCallback(string callBackName, Action<string> callBackAction, IntPtr? successIndicator = null)
    {
        if (successIndicator == IntPtr.Zero) return;
        Triggernometry.RealPlugin.plug.RegisterNamedCallback(
            callBackName,
            new Action<object, string>((_, cmd) => callBackAction(cmd)),
            null,
            registrant: Name
        );
    }

    public static void ScanSignatures()
    {
        ShowTextGimmickHintHelper.Scan();
        QuitInstanceHelper.Scan();
        EntityHelper.Scan();
        VfxHelper.Scan();
        EnvironmentEffectHelper.Scan();
        CameraHelper.Scan();
        UseActionHelper.Scan();
        ExecuteCommandHelper.Scan();
    }

    public static void Init()
    {
        OnXivProcUpdate();
        Triggernometry.Interpreter.StaticHelpers.RegisterXivProcessUpdatedAction(Name, new System.Action(OnXivProcUpdate));

        ShowTextGimmickHintHelper.Register();
        QuitInstanceHelper.Register();
        EntityHelper.Register();
        VfxHelper.Register();
        EnvironmentEffectHelper.Register();
        CameraHelper.Register();
        UseActionHelper.Register();
        ExecuteCommandHelper.Register();
        Echo($"已激活：{Name} v{"${env:version}"}");
    }

    public static void CheckBeforeExecution(string command)
    {
        if (!IsReady)
            throw new Exception("[鲶鱼精邮差扩展] 没有对应的 FFXIV 进程");

        if (string.IsNullOrWhiteSpace(command))
            throw new Exception("[鲶鱼精邮差扩展] 指令为空");
    }

    static void DisableAfkCountDown()
    {
        IntPtr ptr = TryScan("E9 ? ? ? ? F3 0F 10 53 1C", nameof(DisableAfkCountDown)) + 0x23; // 不是跳转！
        byte[] nop = new byte[] {
            0x90, 0x90, 0x90, 0x90, 0x90,    // F3 0F11 53 1C         - movss [rbx+1C],xmm2  副本外计时器
            0x90, 0x90, 0x90, 0x90, 0x90     // F3 0F11 43 14         - movss [rbx+14],xmm0  副本内计时器
        };
        WriteBytes(XivProcHandle, ptr, (uint)nop.Length, nop);
        Log(DebugLevelEnum.Custom, "已禁用 AFK 倒计时。");
    }

    public static VariableDictionary GetConfig()
    {
        var cfg = Triggernometry.Interpreter.StaticHelpers.GetDictVariable(true, "PNE_cfg");
        if (cfg == null)
        {
            cfg = new VariableDictionary();
            Triggernometry.Interpreter.StaticHelpers.SetDictVariable(true, "PNE_cfg", cfg);
        }
        return cfg;
    }

    #endregion Common

    public static class ShowTextGimmickHintHelper
    {
        public static IntPtr GetUiModulePtr;
        public static IntPtr FrameworkPtrPtr;
        public static IntPtr ShowTextGimmickHintPtr;

        public static void Scan()
        {
            var myName = BridgeFFXIV.GetMyself().GetValue("name").ToString();
            FrameworkPtrPtr = TryScanMultiple(new string[] {
                "49 8B C4 48 8B 0D ? ? ? ? 48 8D 15 ? ? ? ? 48 89 05 * * * *", // 7.0 CN
                "49 8B DC 48 89 1D * * * *" // 7.0 global
            }, nameof(FrameworkPtrPtr));
            GetUiModulePtr = TryScan("E8 * * * * 80 7B 1D 01", nameof(GetUiModulePtr));
            ShowTextGimmickHintPtr = TryScan("48 ?? ?? 0F 84 ?? ?? ?? ?? 4C ?? ?? 49 89 5B ?? 49 89 73", nameof(ShowTextGimmickHintPtr));
        }

        public static void Register()
        {
            RegisterCallback("Hint", Hint);
            RegisterCallback("Warn", Warn);
        }

        public static void Hint(string command) => DoShowTextGimmickHint(true, command);
        public static void Warn(string command) => DoShowTextGimmickHint(false, command);
        public static void DoShowTextGimmickHint(bool isInfo, string command)
        {
            CheckBeforeExecution(command);

            string rawTime = command.Substring(0, command.IndexOf('\n')).Trim();
            if (!double.TryParse(rawTime, NumberStyles.Float, CultureInfo.InvariantCulture, out double time))
            {
                throw Context.ParseTypeError("string", rawTime, "double", command);
            }
            int timeIn100Ms = Math.Max(0, (int)(time * 10));
            string text = command.Substring(command.IndexOf('\n') + 1);
            PostNamazuUi?.Log((isInfo ? "Hint" : "Warn") + $" ({timeIn100Ms / 10.0:F1} s): \n{text}");

            ExecuteWithLock(() => {
                _DoShowTextGimmickHint(isInfo, text, timeIn100Ms);
            });
        }

        public static void _DoShowTextGimmickHint(bool isHint, string text, int timeIn100Ms)
        {
            var frameworkPtr = Memory.Read<IntPtr>(FrameworkPtrPtr);
            var uiModulePtr = Memory.CallInjected64<IntPtr>(GetUiModulePtr, frameworkPtr);
            var raptureAtkModule = Memory.CallInjected64<IntPtr>(Memory.Read<IntPtr>(Memory.Read<IntPtr>(uiModulePtr) + (0x8 * 7)), uiModulePtr);
            IntPtr stringPtr = IntPtr.Zero;
            try
            {
                stringPtr = Memory.AllocateMemory(1024);
                Memory.WriteString(stringPtr, text, Encoding.UTF8);
                Memory.CallInjected64<int>(ShowTextGimmickHintPtr, raptureAtkModule, stringPtr, isHint ? 1 : 0, timeIn100Ms);
            }
            finally { if (stringPtr != IntPtr.Zero) Memory.FreeMemory(stringPtr); }
        }
    }

    public static class QuitInstanceHelper
    {
        public static IntPtr QuitInstancePtr;

        public static void Scan()
        {
            QuitInstancePtr = TryScan("48 83 EC ?? 0F B6 D1 45 33 C9", nameof(QuitInstancePtr));
        }

        public static void Register()
        {
            RegisterCallback("Quit", QuitInstance, QuitInstancePtr);
        }

        public static void QuitInstance(string cmd)
        {
            cmd = cmd.Trim().ToLower();
            bool force = cmd == "1" || cmd == "true";
            ExecuteWithLock(() => Memory.CallInjected64<IntPtr>(QuitInstancePtr, force ? 1 : 0));
        }
    }

    public static class EntityHelper
    {
        public static IntPtr TargetSystemPtr;
        public static IntPtr HasStatusPtr;
        public static IntPtr GetStatusIndexPtr;
        public static IntPtr RemoveStatusPtr;

        public static void Scan()
        {
            TargetSystemPtr = TryScan("48 8D 0D * * * * E8 ? ? ? ? 48 3B C6 0F 95 C0", nameof(TargetSystemPtr));
            HasStatusPtr = TryScan("E8 * * * * C6 43 2D 00", nameof(HasStatusPtr));
            GetStatusIndexPtr = TryScan("E8 * * * * 85 C0 79 21", nameof(GetStatusIndexPtr));
            RemoveStatusPtr = TryScan("83 FA 3C 73 50", nameof(RemoveStatusPtr));
        }

        public static void Register()
        {
            Triggernometry.Interpreter.StaticHelpers.Storage["_SetPos"] = (object)_SetPos;
            RegisterCallback("InvokeOnMultipleEntities", InvokeOnMultipleEntities);
            RegisterCallback("SetHeading", SetHeading);
            RegisterCallback("Target", Target, TargetSystemPtr);
            RegisterCallback("SetModelStatus", SetModelStatus);
            RegisterCallback("SetObjectScale", SetObjectScale);
            RegisterCallback("ObjectScaling", ObjectScaling);
            RegisterCallback("SetOpacity", SetOpacity);
            RegisterCallback("SetOpacityForOtherPlayers", SetOpacityForOtherPlayers);
            RegisterCallback("Hide", Hide);
            RegisterCallback("HideOtherPlayers", HideOtherPlayers);
            RegisterCallback("SetStatusLoopVfx", SetStatusLoopVfx);
            RegisterCallback("Redraw", Redraw);
            RegisterCallback("SetHighlightColor", SetHighlightColor);
            RegisterCallback("RemoveStatus", RemoveStatus, RemoveStatusPtr);
        }

        public static void InvokeOnMultipleEntities(string cmd)
        {
            var cmds = cmd.Split(new[] { '\n' }, StringSplitOptions.RemoveEmptyEntries);
            string entityFilter = cmds[0];
            foreach (IntPtr address in Triggernometry.FFXIV.Entity.GetFilteredEntities(entityFilter).Select(e => e.Address))
            {
                foreach (var cbPair in cmds.Skip(1).Select(c => c.Split(new[] { ',' }, 2)))
                {
                    if (cbPair.Length == 1) throw new Exception($"批量调用回调时未提供回调参数：{cbPair[0]}");
                    var name = cbPair[0].Trim();
                    var param = cbPair[1].Replace("_address", address.ToString());
                    Task.Run(() =>
                    {
                        try
                        {
                            RealPlugin.plug.InvokeNamedCallback(name, param);
                        }
                        catch (Exception ex)
                        {
                            Log(RealPlugin.DebugLevelEnum.Warning, $"批量调用回调时失败：{name} -> {param} \n{ex}");
                        }
                    });
                }
            }
        }

        public static void SetHeading(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            float h = float.Parse(args[0], NumberStyles.Float, CultureInfo.InvariantCulture);
            ExecuteWithLock(() => _SetHeading(objectPtr, h));
        }

        public static void Target(string cmd)
        {
            cmd = cmd.Trim().ToUpper();
            if (cmd == "E0000000")
            {
                _Target(IntPtr.Zero);
                return;
            }
            uint id = uint.Parse(cmd, NumberStyles.HexNumber, CultureInfo.InvariantCulture);
            var entity = Triggernometry.FFXIV.Entity.GetEntityByID(id);
            if (!entity.Exist)
            {
                throw new Exception($"未找到实体：{id:X}");
            }
            _Target(entity.Address);
        }

        public static void SetModelStatus(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            // 0: "visible" 正常状态
            // 512/1024: 玩家切换地图时会经历的两种状态，类似 16384
            // 2048: 不重绘: 有模型无名牌、列表可选；重绘：恢复 0
            // 4096: 不重绘: 有模型无名牌、列表可选；重绘：不变，刷新模型
            // 8192: 不重绘：有模型无名牌、不可选；重绘/移动/攻击：恢复 0
            // 16384: 不重绘：有模型无名牌、不可选；重绘：不变，刷新模型
            int value = (int)MathParser.Parse(args[0]);
            ExecuteWithLock(() => _SetModelStatus(objectPtr, value));
        }

        // 新方法 直接修改实体参数并重绘
        public static void SetObjectScale(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            float scale = float.Parse(args[0], NumberStyles.Float, CultureInfo.InvariantCulture);
            ExecuteWithLock(() => _SetObjectScale(objectPtr, scale));
        }

        // 旧方法 临时修改
        public static void ObjectScaling(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            float scaleX = float.Parse(args[0], NumberStyles.Float, CultureInfo.InvariantCulture);
            float scaleY = float.Parse(args.Length > 1 ? args[1] : args[0], NumberStyles.Float, CultureInfo.InvariantCulture);
            float scaleZ = float.Parse(args.Length > 2 ? args[2] : args[0], NumberStyles.Float, CultureInfo.InvariantCulture);
            ExecuteWithLock(() => _ObjectScalingTemp(objectPtr, scaleX, scaleY, scaleZ));
        }

        public static void SetOpacity(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            float opacity = float.Parse(args[0], NumberStyles.Float, CultureInfo.InvariantCulture);
            ExecuteWithLock(() => _SetOpacity(objectPtr, opacity));
        }

        public static void SetOpacityForOtherPlayers(string cmd)
        {
            float opacity = float.Parse(cmd, NumberStyles.Float, CultureInfo.InvariantCulture);
            var addresses = BridgeFFXIV.GetAllEntities()
                .Where(c => (c.GetValue("id").ToString().StartsWith("10") && c.GetValue("id").ToString() != BridgeFFXIV.PlayerHexId)
                     || c.GetValue("ownerid").ToString().StartsWith("10"))
                .Select(c => (IntPtr)long.Parse(c.GetValue("address").ToString()));
            ExecuteWithLock(() => {
                foreach (var address in addresses)
                {
                    _SetOpacity(address, opacity);
                }
            });
        }

        public static void Hide(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            bool hide = bool.TryParse(args[0].ToLower(), out hide) ? hide : (!MathParser.IsZero(MathParser.Parse(args[0])));
            ExecuteWithLock(() => _Hide(objectPtr, hide));
        }

        public static void HideOtherPlayers(string cmd)
        {
            bool hide = bool.TryParse(cmd.ToLower(), out hide) ? hide : (!MathParser.IsZero(MathParser.Parse(cmd)));

            var addresses = BridgeFFXIV.GetAllEntities()
                .Where(c => (c.GetValue("id").ToString().StartsWith("10") && c.GetValue("id").ToString() != BridgeFFXIV.PlayerHexId)
                     || c.GetValue("ownerid").ToString().StartsWith("10"))
                .Select(c => (IntPtr)long.Parse(c.GetValue("address").ToString()));

            ExecuteWithLock(() => {
                foreach (var address in addresses)
                {
                    _Hide(address, hide);
                }
            });
        }

        public static void SetStatusLoopVfx(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            ushort vfxId = ushort.Parse(args[0]);
            ExecuteWithLock(() => {
                _SetStatusLoopVfx(objectPtr, vfxId);
                _ReDraw(objectPtr);
            });
        }

        public static void Redraw(string address)
        {
            IntPtr entityPtr = (IntPtr)long.Parse(address);
            ExecuteWithLock(() => _ReDraw(entityPtr));
        }

        public static void SetHighlightColor(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            byte color = byte.Parse(args[0]);
            ExecuteWithLock(() => _SetHighlightColor(objectPtr, color));
        }

        public static void RemoveStatus(string cmd)
        {
            var args = ParseArgs_OutAddress(cmd, out IntPtr objectPtr);
            ushort statusId = (ushort)MathParser.Parse(args[0]);
            ExecuteWithLock(() => _RemoveStatus(objectPtr, statusId));
        }

        internal static void _SetPos(IntPtr objectAddress, float x, float y, float z)
        {
            Vector3 pos = new Vector3(x, z, y); // 注意 Y Z 轴交换
            IntPtr modelAddress = Memory.Read<IntPtr>(objectAddress + 0x100);
            Memory.Write(objectAddress + 0xB0, pos);
            Memory.Write(modelAddress + 0x50, pos);
        }

        internal static void _SetHeading(IntPtr objectAddress, float h)
        {
            IntPtr modelAddress = Memory.Read<IntPtr>(objectAddress + 0x100);
            Memory.Write(objectAddress + 0xC0, h);
            // 两个数都和面向有关，公式是凑出来的，似乎是个四元数？
            Memory.Write(modelAddress + 0x64, (float)Math.Sin(h / 2));
            Memory.Write(modelAddress + 0x6C, (float)Math.Cos(h / 2));
        }

        internal static void _Target(IntPtr address)
        {   // FFXIVClientStructs/FFXIV/Client/Game/Control/TargetSystem.cs
            Memory.Write(TargetSystemPtr + 0x80, address);
            Memory.Write(TargetSystemPtr + 0x88, address);
        }

        internal static void _SetModelStatus(IntPtr objectAddress, int status)
        {
            Memory.Write(objectAddress + VersionHelper.ModelStatusOffset(), status);
        }

        internal static void _ObjectScalingTemp(IntPtr objectAddress, float scaleX, float scaleY, float scaleZ)
        {
            uint id = Memory.Read<uint>(objectAddress + 0x74);
            if (BridgeFFXIV.PlayerId == id) // 调整视角高度
            {
                Memory.Write<float>(objectAddress + 0xC4, (scaleZ + 1) / 2f);
            }
            IntPtr drawObjectAddress = Memory.Read<IntPtr>(objectAddress + 0x100);
            Memory.Write<float>(drawObjectAddress + 0x70, scaleX);
            Memory.Write<float>(drawObjectAddress + 0x74, scaleZ);
            Memory.Write<float>(drawObjectAddress + 0x78, scaleY);
        }

        internal static void _SetObjectScale(IntPtr objectAddress, float scale)
        {
            Memory.Write<float>(objectAddress + 0xC4, scale);
            _ReDraw(objectAddress);
        }

        internal static void _SetOpacity(IntPtr objectAddress, float opacity)
        {
            Memory.Write<float>(objectAddress + VersionHelper.OpacityOffset(), opacity);
        }

        internal static void _Hide(IntPtr objectAddress, bool hide)
        {
            Memory.Write<byte>(objectAddress + 0x118, (byte)(hide ? 2 : 0));
        }

        internal static void _SetStatusLoopVfx(IntPtr objectAddress, ushort id)
        {
            Memory.Write<ushort>(objectAddress + VersionHelper.StatusLoopVfxOffset(), id);
            _ReDraw(objectAddress);
        }

        internal static void _EnableDraw(IntPtr address)
            => CallVirtualFunction(address, 12);

        internal static void _DisableDraw(IntPtr address)
            => CallVirtualFunction(address, 13);

        internal static void _ReDraw(IntPtr address)
        {
            _DisableDraw(address);
            _EnableDraw(address);
        }

        internal static void _SetHighlightColor(IntPtr address, byte color)
            => CallVirtualFunction(address, 26, color);

        // FFXIVClientStructs/FFXIV/Client/Game/Character/Character.cs
        // The GameObject must be a Character!
        internal static IntPtr _GetStatusManagerPtr(IntPtr address)
            => CallVirtualFunction<IntPtr>(address, 77);

        internal static void _RemoveStatus(IntPtr address, ushort statusId)
        {
            IntPtr statusManagerPtr = _GetStatusManagerPtr(address);
            if (statusManagerPtr == IntPtr.Zero) throw new Exception($"StatusManagerPtr is null for entity at {(long)address:X}");
            int statusIndex = Memory.CallInjected64<int>(GetStatusIndexPtr, statusManagerPtr, statusId, 0xE0000000);
            if (statusIndex < 0) throw new Exception($"Status 0x{statusId:X} does not exist for entity at {(long)address:X}");
            Memory.CallInjected64<IntPtr>(RemoveStatusPtr, statusManagerPtr, statusIndex, 0);
        }
    }

    public static class VfxHelper
    {
        public static IntPtr ActorVfxCreatePtr;
        public static IntPtr ActorVfxRemovePtr;

        public static void Scan()
        {
            ActorVfxCreatePtr = TryScan("E8 * * * * 48 8B D8 48 85 C0 74 ?? 0F B6 57 ?? 48 8B C8 C0 EA 02 80 E2 01", nameof(ActorVfxCreatePtr));
            var actorVfxRemovePtrPtr = TryScan("0F 11 48 10 48 8D 05 * * * *", nameof(ActorVfxRemovePtr));
            ActorVfxRemovePtr = Memory.Read<IntPtr>(actorVfxRemovePtrPtr);
        }

        public static void Register()
        {
            RegisterCallback("LockOn", VfxHelper.LockOn, ActorVfxCreatePtr);
            RegisterCallback("Channeling", VfxHelper.Channeling, ActorVfxCreatePtr);
        }

        public static void LockOn(string cmd)
        {
            string[] args = ParseArgs_OutAddress(cmd, out IntPtr tgtAddress);
            string vfxName = args[0];
            ExecuteWithLock(() => _LockOn(tgtAddress, vfxName));
        }

        private static IntPtr _LockOn(IntPtr tgtAddress, string vfxName)
        {
            string path = $"vfx/lockon/eff/{vfxName}.avfx";
            IntPtr pathPtr = IntPtr.Zero;
            try
            {
                pathPtr = Memory.AllocateMemory(256);
                Memory.WriteString(pathPtr, path, Encoding.UTF8);
                IntPtr vfxHandle = Memory.CallInjected64<IntPtr>(
                    ActorVfxCreatePtr,
                    pathPtr,    // vfx name
                    tgtAddress, // caster 
                    tgtAddress, // target 
                    -1f, /*(char)*/(ushort)0, 0, /*(char)*/(ushort)0
                );
                return vfxHandle;
            }
            finally { if (pathPtr != IntPtr.Zero) Memory.FreeMemory(pathPtr); }
        }

        public static void Channeling(string cmd)
        {
            string[] args = ParseArgs_OutAddress2(cmd, out IntPtr srcAddress, out IntPtr tgtAddress);
            string vfxName = args[0];
            double duration = args.Length > 1 ? double.Parse(args[1], NumberStyles.Float, CultureInfo.InvariantCulture) : 3.0;

            IntPtr vfxHandle = ExecuteWithLock(() => _Channeling(srcAddress, tgtAddress, vfxName));
            Thread.Sleep((int)(duration * 1000));
            if (vfxHandle != IntPtr.Zero)
            {
                ExecuteWithLock(() => _ActorVfxRemove(vfxHandle));
            }
            else
            {
                throw new Exception($"Remove channeling failed: {vfxName}");
            }
        }

        private static IntPtr _Channeling(IntPtr srcAddress, IntPtr tgtAddress, string vfxName)
        {
            string path = $"vfx/channeling/eff/{vfxName}.avfx";
            IntPtr pathPtr = IntPtr.Zero;
            try
            {
                pathPtr = Memory.AllocateMemory(256);
                Memory.WriteString(pathPtr, path, Encoding.UTF8);
                IntPtr vfxHandle = Memory.CallInjected64<IntPtr>(
                    ActorVfxCreatePtr,
                    pathPtr,    // vfx name
                    srcAddress, // caster 
                    tgtAddress, // target 
                    -1f, /*(char)*/(ushort)0, 0, /*(char)*/(ushort)0
                );
                return vfxHandle;
            }
            finally { if (pathPtr != IntPtr.Zero) Memory.FreeMemory(pathPtr); }
        }

        private static void _ActorVfxRemove(IntPtr vfxHandle)
        {
            Memory.CallInjected64<IntPtr>(ActorVfxRemovePtr, vfxHandle, /*(char)*/(ushort)1);
        }

    }

    public static class EnvironmentEffectHelper
    {
        public static IntPtr MapEffectFunctionPtr;
        public static IntPtr EventFrameworkPtrPtr;
        public static IntPtr EnvManagerPtrPtr;

        public static void Scan()
        {
            MapEffectFunctionPtr = TryScan("E8 * * * * 48 8B 5C 24 ?? 48 83 C4 ?? 5F C3 CC CC CC CC CC CC CC CC CC CC CC CC CC CC 48 89 5C 24 ?? 57 48 83 EC ?? 0F B7 DA 33 C0", nameof(MapEffectFunctionPtr));
            EventFrameworkPtrPtr = TryScan("4C 39 2D * * * * 74 14", nameof(EventFrameworkPtrPtr));
            EnvManagerPtrPtr = TryScan("0F 28 F2 48 8B 05 * * * *", nameof(EnvManagerPtrPtr));
        }

        public static void Register()
        {
            RegisterCallback("MapEffect", MapEffect, EventFrameworkPtrPtr);
            RegisterCallback("ChangeWeather", ChangeWeather, EnvManagerPtrPtr);
        }

        public static void MapEffect(string command)
        {
            CheckBeforeExecution(command);
            uint position;
            ushort data1, data2;

            if (command.Contains(",")) // given 3 parameters
            {
                var rawArgs = Context.SplitArguments(command);
                if (!uint.TryParse(rawArgs[0], NumberStyles.Integer, CultureInfo.InvariantCulture, out position))
                {
                    throw Context.ParseTypeError("string", rawArgs[0], "uint", command);
                }
                if (!ushort.TryParse(rawArgs[1], NumberStyles.Integer, CultureInfo.InvariantCulture, out data1))
                {
                    throw Context.ParseTypeError("string", rawArgs[1], "ushort", command);
                }
                if (!ushort.TryParse(rawArgs[2], NumberStyles.Integer, CultureInfo.InvariantCulture, out data2))
                {
                    throw Context.ParseTypeError("string", rawArgs[2], "ushort", command);
                }
            }
            else // given act or network log format
            {
                string pattern = @"^(?<data2>[0-9A-Fa-f]{4})(?<data1>[0-9A-Fa-f]{4})[:|](?<position>[0-9A-Fa-f]{1,8})$";
                var match = Regex.Match(command.Trim(), pattern);
                if (match.Success)
                {
                    data2 = ushort.Parse(match.Groups["data2"].Value, NumberStyles.HexNumber, CultureInfo.InvariantCulture);
                    data1 = ushort.Parse(match.Groups["data1"].Value, NumberStyles.HexNumber, CultureInfo.InvariantCulture);
                    position = uint.Parse(match.Groups["position"].Value, NumberStyles.HexNumber, CultureInfo.InvariantCulture);
                }
                else
                {
                    throw new Exception($"Invalid mapeffect parameters: {command}");
                }
            }

            PostNamazuUi?.Log($"MapEffect: position = {position}, data1 = {data1}, data2 = {data2} ({data2:X4}{data1:X4}:{position:X2})");
            ExecuteWithLock(() => _MapEffect(position, data1, data2));
        }

        static void _MapEffect(uint position, ushort data1, ushort data2)
        {
            var mapEffectModulePtr = Memory.Read<IntPtr>(Memory.Read<IntPtr>(EventFrameworkPtrPtr) + 0x158);
            Memory.CallInjected64<IntPtr>(MapEffectFunctionPtr, mapEffectModulePtr, position, data1, data2);
        }

        public static void ChangeWeather(string command)
        {
            CheckBeforeExecution(command);
            byte weatherId = byte.Parse(command, NumberStyles.Integer, CultureInfo.InvariantCulture);

            PostNamazuUi?.Log($"ChangeWeather: weatherId = {weatherId}");
            ExecuteWithLock(() => _ChangeWeather(weatherId));
        }

        // FFXIVClientStructs/FFXIV/Client/Graphics/Environment/EnvManager.cs
        static void _ChangeWeather(byte weatherId)
        {
            var envManagerPtr = Memory.Read<IntPtr>(EnvManagerPtrPtr);
            Memory.Write<byte>(envManagerPtr + 0x27, weatherId); // ActiveWeather
            Memory.Write<float>(envManagerPtr + 0x28, 1); // TransitionTime
        }
    }

    public static class CameraHelper
    {
        public static void Scan()
        {
        }

        public static void Register()
        {
            RegisterCallback("SetCameraParams", SetCameraParams);
            try
            {
                // 01 行触发初始化的时候这行会报错？因为 CameraAddress 还没初始化？
                RealPlugin.plug.InvokeNamedCallback("SetCameraParams", "apply");
            }
            catch { }
        }

        public static IntPtr CameraAddress = Triggernometry.Utilities.Memory.GetCameraAddress();
        public static Dictionary<string, int> Offsets = new Dictionary<string, int>(StringComparer.OrdinalIgnoreCase)
        {
            { "Distance", 0x114 },
            { "MinDistance", 0x118 },
            { "MaxDistance", 0x11C },
            { "FoV", 0x120 },
            { "MinFoV", 0x124 },
            { "MaxFoV", 0x128 },
            { "AngleH", 0x130 }, // 这个角度似乎不是底层的数值，手动修改（增加或减少）时，改变量的绝对值 dθ 会变为 max(dθ - pi/40, 0)
            { "AngleV", 0x134 }, // 上 -pi/2   下 pi/2
            { "MinAngleV", 0x148 },
            { "MaxAngleV", 0x14C },
        };

        // 游戏默认
        public static Dictionary<string, float> OriginalParams = new Dictionary<string, float>(StringComparer.OrdinalIgnoreCase)
        {
            { "MinDistance", 1.5f },
            { "MaxDistance", 20.0f },
            { "MinFoV", 0.68f },
            { "MaxFoV", 0.78f },
            { "MinAngleV", -1.483529806f },
            { "MaxAngleV", 0.7853981853f },
        };

        public static Dictionary<string, float> DefaultEditedParams = new Dictionary<string, float>(StringComparer.OrdinalIgnoreCase)
        {
            { "MinDistance", 0.5f },
            { "MaxDistance", 9999f },
            { "MinFoV", 0.68f },
            { "MaxFoV", 0.78f },
            { "MinAngleV", -1.569f }, // 超过这个数值时第一人称视角难以旋转
            { "MaxAngleV", 1.569f },  // 超过这个数值时视角会反转
        };

        public static float AngleV
        {
            get => Memory.Read<float>(CameraAddress + Offsets["AngleV"]);
            set => Memory.Write<float>(CameraAddress + Offsets["AngleV"], value);
        }

        public static float AngleH // 和游戏的角度（南 = 0）是反的，补偿 pi
        {
            get
            {
                var actualValue = Memory.Read<float>(CameraAddress + Offsets["AngleH"]);
                var convertedValue = MathParser.ModFunction(actualValue, 2 * Math.PI) - Math.PI;
                return (float)convertedValue;
            }
            set // 这个角度似乎不是底层的数值，手动修改（增加或减少）时，改变量的绝对值 θ 会变为 max(θ - pi/40, 0)
            {
                double errθ = Math.PI / 40;
                double oldθ = Memory.Read<float>(CameraAddress + Offsets["AngleH"]);
                double newθ = MathParser.ModFunction(value, 2 * Math.PI) - Math.PI;  // 补偿
                double dθ = MathParser.ModFunction(newθ - oldθ + Math.PI, 2 * Math.PI) - Math.PI;
                if (Math.Abs(dθ) >= 3.05)
                {
                    Memory.Write<float>(CameraAddress + Offsets["AngleH"], (float)oldθ + Math.Sign(dθ));
                    oldθ += Math.Sign(dθ) * (1 - errθ); // 实际变化的量
                    dθ = MathParser.ModFunction(newθ - oldθ + Math.PI, 2 * Math.PI) - Math.PI;
                }
                double writeValue = newθ + Math.Sign(dθ) * errθ;
                Memory.Write<float>(CameraAddress + Offsets["AngleH"], (float)writeValue);
            }
        }

        private static float GetCameraParam(string param)
        {
            if (Offsets.TryGetValue(param, out int offset))
            {
                return Memory.Read<float>(CameraAddress + offset);
            }
            else
            {
                Log(DebugLevelEnum.Error, $"错误的相机参数 ({param})。");
                return default;
            }
        }

        private static void SetCameraParam(string param, float newValue)
        {
            switch (param.ToLower())
            {
                case "angleh": AngleH = newValue; break;
                case "anglev": AngleV = newValue; break;
                default:
                    if (Offsets.TryGetValue(param, out int offset))
                    {
                        IntPtr address = CameraAddress + offset;
                        Memory.Write<float>(address, newValue);
                        Log(DebugLevelEnum.Custom, $"成功设置相机参数 {param} = {newValue}");
                    }
                    else
                    {
                        Log(DebugLevelEnum.Error, $"错误的相机参数 ({param})。");
                    };
                    break;
            }
        }

        /// <summary> 接收单个关键词：reset apply clearconfig initconfig; <br />
        /// 或每行一个指定修改的数据，如 Distance = 10 </summary>
        public static void SetCameraParams(string cmd)
        {
            switch (cmd.Trim().ToLower())
            {
                case "reset": // 临时重置当前视距、视角及其范围为游戏默认值
                    foreach (var kvp in OriginalParams)
                    {
                        SetCameraParam(kvp.Key, kvp.Value);
                    }
                    break;
                case "apply": // 将配置中的当前视距、视角及其范围应用到游戏
                    foreach (var kvp in OriginalParams)
                    {
                        var rawValue = GetConfig().GetValue($"camera_{kvp.Key}").ToString();
                        if (string.IsNullOrWhiteSpace(rawValue)) continue;
                        var newValue = float.Parse(rawValue, NumberStyles.Float, CultureInfo.InvariantCulture);
                        SetCameraParam(kvp.Key, newValue);
                    }
                    break;
                case "clearconfig": // 清除相关配置，并恢复游戏默认值
                    foreach (var kvp in OriginalParams)
                    {
                        GetConfig().Values.Remove($"camera_{kvp.Key}");
                        SetCameraParam(kvp.Key, kvp.Value);
                    }
                    break;
                case "initconfig": // 初始化相关配置并应用到游戏
                    foreach (var kvp in DefaultEditedParams)
                    {
                        GetConfig().SetValue($"camera_{kvp.Key}", kvp.Value);
                        SetCameraParam(kvp.Key, kvp.Value);
                    }
                    break;
                default:
                    var kvps = cmd.Split('\n').Select(data => data.Split(new char[] { '=' }, 2)).Where(data => data.Length == 2);
                    foreach (var kvp in kvps)
                    {
                        var key = kvp[0].Trim();
                        if (!Offsets.ContainsKey(key)) continue;
                        var value = (float)MathParser.Parse(kvp[1]);
                        SetCameraParam(key, value);
                    }
                    break;
            }
        }
    }

    public static class UseActionHelper
    {
        public static IntPtr UseActionPtr;
        public static IntPtr UseActionLocationPtr;
        public static IntPtr ActionManagerPtr;

        public static void Scan()
        {
            UseActionPtr = TryScan("E8 * * * * B0 01 EB B6", nameof(UseActionPtr));
            UseActionLocationPtr = TryScan("E8 * * * * 41 3A C5 0F 85", nameof(UseActionLocationPtr));
            ActionManagerPtr = TryScan("48 8D 0D * * * * F3 0F 10 13", nameof(ActionManagerPtr));
        }

        public static void Register()
        {
            RegisterCallback("UseAction", UseAction, UseActionPtr);
            RegisterCallback("UseActionLocation", UseActionLocation, UseActionLocationPtr);
        }

        public enum ActionType : byte
        {
            None = 0,
            Normal = 1, Action = 1, // Spell, Weaponskill, Ability. Confusing name, I know.
            Item = 2,
            KeyItem = 3,
            Ability = 4, // Not in UseActionHelper (??)
            General = 5, GeneralAction = 5,
            Buddy = 6, BuddyAction = 6,
            Main = 7, MainCommand = 7,
            Companion = 8,
            Craft = 9, CraftAction = 9,
            Unk_10 = 10, // Fishing per Sapphire? Something to do with items.
            Pet = 11, PetAction = 11,
            Unk_12 = 12, // Not in UseActionHelper. Sapphire says CompanyAction, but not actually triggered.
            Mount = 13,
            PvP = 14, PvPAction = 14,
            Waymark = 15, FieldMarker = 15,
            ChocoboRaceAbility = 16,
            ChocoboRaceItem = 17,
            Unk_18 = 18, // Not in UseActionHelper (?)
            BgcArmyAction = 0x19,
            Ornament = 0x20,
        }

        public enum UseActionMode
        {
            None = 0, // usual action execution, e.g. a hotbar button press
            Queue = 1, // previously queued action is now ready and is being executed (=> will ignore queue)
            Macro = 2, // action execution originating from a macro (=> won't be queued)
            Combo = 3, // action execution is from a single-button combo
        }

        public static void UseAction(string command)
        {
            CheckBeforeExecution(command);

            var args = Context.SplitArguments(command);
            ActionType actionType = Enum.TryParse(args[0], out actionType) ? actionType : (ActionType)(int)MathParser.Parse(args[0]);
            uint actionId = (uint)MathParser.Parse(args[1]);
            ulong targetId = (ulong)MathParser.Parse(Context.GetArgument(args, 2, "0xE0000000", true));
            uint extraParam = (uint)(actionType == ActionType.Item ? 0xFFFF : 0);
            UseActionMode mode = Enum.TryParse(Context.GetArgument(args, 3, "0"), out mode) ? mode
                : (UseActionMode)(int)MathParser.Parse(Context.GetArgument(args, 3, "0"));
            uint comboRouteID = 0;

            var assemblyLock = Memory.Executor.AssemblyLock;
            var flag = false;
            try
            {
                Monitor.Enter(assemblyLock, ref flag);
                if (Memory.CallInjected64<bool>(UseActionPtr, ActionManagerPtr, (int)actionType, actionId, targetId, extraParam, (int)mode, comboRouteID, 0))
                {
                    PostNamazuUi?.Log($"UseAction: {actionType} ({(int)actionType}), id = {actionId} (0x{actionId:X}), target = {targetId:X}, mode = {mode} ({(int)mode})");
                }
            }
            finally
            {
                if (flag) Monitor.Exit(assemblyLock);
            }
        }

        public static void UseActionLocation(string command)
        {
            CheckBeforeExecution(command);

            var args = Context.SplitArguments(command);
            ActionType actionType = Enum.TryParse(args[0], out actionType) ? actionType : (ActionType)(int)MathParser.Parse(args[0]);
            uint actionId = (uint)MathParser.Parse(args[1]);
            ulong targetId = 0xE0000000;
            var pos = new Vector3((float)MathParser.Parse(args[2]), (float)MathParser.Parse(args[4]), (float)MathParser.Parse(args[3]));
            uint extraParam = (uint)MathParser.Parse(Context.GetArgument(args, 5, "0", true));

            var assemblyLock = Memory.Executor.AssemblyLock;
            var flag = false;
            try
            {
                Monitor.Enter(assemblyLock, ref flag);
                IntPtr posPtr = Memory.AllocateMemory(0x10);
                Memory.Write(posPtr, pos);
                if (Memory.CallInjected64<bool>(UseActionLocationPtr, ActionManagerPtr, (int)actionType, actionId, targetId, posPtr, extraParam))
                {
                    PostNamazuUi?.Log($"UseAction (Ground): {actionType} ({(int)actionType}); id = {actionId} (0x{actionId:X}); Pos = ({pos.X:0.##}, {pos.Z:0.##}), {pos.Y:0.##}");
                }
            }
            finally
            {
                if (flag) Monitor.Exit(assemblyLock);
            }
        }
    }

    public static class ExecuteCommandHelper
    {
        public static IntPtr ExecuteCommandPtr;
        public static IntPtr ExecuteCommandTgtPtr;
        public static IntPtr ExecuteCommandPosPtr;

        public static void Scan()
        {
            ExecuteCommandPtr = TryScan("E8 * * * * 48 83 C4 ? C3 CC CC CC CC CC CC CC CC CC CC CC CC 48 83 EC ? 45 0F B6 C0", nameof(ExecuteCommandPtr));
            ExecuteCommandTgtPtr = TryScan("E8 * * * * 80 7D ? ? 74 ? 41 0F B6 45", nameof(ExecuteCommandTgtPtr)); // DR: ExecuteCommandComplex
            ExecuteCommandPosPtr = TryScan("E8 * * * * EB ? 48 8B 53 ? 33 C0", nameof(ExecuteCommandPosPtr)); // DR: ExecuteCommandComplexLocation
        }

        public static void Register()
        {
            Triggernometry.Interpreter.StaticHelpers.Storage["_ExecuteCommand"] = (object)_ExecuteCommand;
            Triggernometry.Interpreter.StaticHelpers.Storage["_ExecuteCommandTgt"] = (object)_ExecuteCommandTgt;
            Triggernometry.Interpreter.StaticHelpers.Storage["_ExecuteCommandPos"] = (object)_ExecuteCommandPos;
            RegisterCallback("StatusOff", StatusOff, ExecuteCommandPtr);
        }

        internal static bool _ExecuteCommand(uint command, uint param1, uint param2, uint param3, uint param4)
        {
            return Memory.CallInjected64<bool>(ExecuteCommandPtr, command, param1, param2, param3, param4);
        }

        internal static bool _ExecuteCommandTgt(uint command, uint targetId, uint param1, uint param2, uint param3, uint param4)
        {
            return Memory.CallInjected64<bool>(ExecuteCommandTgtPtr, command, targetId, param1, param2, param3, param4);
        }

        internal static bool _ExecuteCommandPos(uint command, double x, double y, double z, uint param1, uint param2, uint param3, uint param4)
        {
            var pos = new Vector3((float)x, (float)z, (float)y);
            IntPtr posPtr = Memory.AllocateMemory(0x10);
            Memory.Write(posPtr, pos);
            return Memory.CallInjected64<bool>(ExecuteCommandPosPtr, command, posPtr, param1, param2, param3, param4);
        }

        public static void StatusOff(string cmd)
        {
            var args = Context.SplitArguments(cmd);
            ushort statusID = (ushort)MathParser.Parse(args[0]);
            uint srcID = uint.Parse(Context.GetArgument(args, 1, "E0000000", true), NumberStyles.HexNumber, CultureInfo.InvariantCulture);
            StatusOff(statusID, srcID);
        }

        internal static void StatusOff(uint statusID, uint srcID = 0xE0000000)
        {
            _ExecuteCommand(104, statusID, 0, srcID, 0);
        }
    }

    #region Utils

    public static void ExecuteWithLock(System.Action action) => NamazuModule.ExecuteWithLock(action);
    public static T ExecuteWithLock<T>(Func<T> function) => NamazuModule.ExecuteWithLock(function);

    static IntPtr TryScan(string pattern, string name)
    {
        try
        {
            return Scanner.ScanText(pattern, name);
        }
        catch (Exception ex)
        {
            RealPlugin.plug.FilteredAddToLog(RealPlugin.DebugLevelEnum.Error, ex.Message);
            return IntPtr.Zero;
        }
    }

    static IntPtr TryScanMultiple(IEnumerable<string> patterns, string name)
    {
        Exception ex = null;
        foreach (var pattern in patterns)
        {
            try
            {
                return Scanner.ScanText(pattern, name);
            }
            catch (Exception e)
            {
                ex = e;
            }
        }
        RealPlugin.plug.FilteredAddToLog(RealPlugin.DebugLevelEnum.Error, ex?.Message ?? "");
        return IntPtr.Zero;
    }

    private static string[] ParseArgs_OutAddress(string cmd, out IntPtr address)
    {
        var args = Context.SplitArguments(cmd);
        address = (IntPtr)long.Parse(args[0]);
        return args.Skip(1).ToArray();
    }

    private static string[] ParseArgs_OutAddress2(string cmd, out IntPtr address1, out IntPtr address2)
    {
        var args = Context.SplitArguments(cmd);
        address1 = (IntPtr)long.Parse(args[0]);
        address2 = (IntPtr)long.Parse(args[1]);
        return args.Skip(2).ToArray();
    }

    public static void CallVirtualFunction(IntPtr objAddress, int vFuncIndex, params object[] args)
        => CallVirtualFunction<IntPtr>(objAddress, vFuncIndex, args);

    public static T CallVirtualFunction<T>(IntPtr objAddress, int vFuncIndex, params object[] args) where T : struct
    {
        IntPtr vTablePtr = Memory.Read<IntPtr>(objAddress);
        IntPtr vFuncPtr = Memory.Read<IntPtr>(vTablePtr + 8 * vFuncIndex);
        return Memory.CallInjected64<T>(vFuncPtr, new object[] { objAddress }.Concat(args).ToArray());
    }

    public static void Echo(string msg) => RealPlugin.plug.InvokeNamedCallback("command", $"/e {msg}");

    public static void Log(RealPlugin.DebugLevelEnum level, string msg) => Interpreter.StaticHelpers.Log(level, msg);

    #endregion Utils
}

