RealPlugin.plug.RegisterNamedCallback("PartyList", new Action<object, string>(PartyListHelper.Start), null);

public static class PartyListHelper
{
    public static void Start(object _, string rawPlayerDescriptions)
    {
        StaticHelpers.SetScalarVariable(false, "PartyListForm_isRunning", "1");
        try
        {
            string[] playerDescriptions = Context.SplitArguments(rawPlayerDescriptions);
            if (playerDescriptions.Length < 1) throw new ArgumentException("PartyList: 未提供初始化小队列表所需的描述文本参数。");
            Thread staThread = new Thread(() => new PartyListForm(playerDescriptions).Run());
            staThread.SetApartmentState(ApartmentState.STA);
            staThread.Start();
            staThread.Join();
        }
        finally
        {
            StaticHelpers.SetScalarVariable(false, "PartyListForm_isRunning", null);
        }
    }
}

#region PartyListForm
public class PartyListForm : Form
{
    public static Font UserFont = CreateFont(10, "Microsoft YaHei", "Microsoft JhengHei");

    PartyListPanel partyListPanel;

    /// <summary> The Panel that contains all the option groups on the form, scrollable. </summary>
    Panel mainPanel = new Panel()
    {
        AutoSize = true,
        AutoSizeMode = AutoSizeMode.GrowAndShrink,
        Dock = DockStyle.Top,
        AutoScroll = true,
    };

    /// <summary> The TableLayoutPanel which contains buttons and other controls at the bottom of the form. </summary>
    TableLayoutPanel bottomPanel = new BottomTableLayoutPanel();

    public Button btnSave = new MyButton { Text = "保存" };

    public PartyListForm(params string[] playerDescriptions)
    {
        // SuspendLayout until Run()
        SuspendLayout();
        // basic
        Text = Info.Name;
        Font = UserFont;
        TopMost = true;
        StartPosition = FormStartPosition.CenterScreen;
        int width = (TextRenderer.MeasureText("啊啊啊啊啊", Font).Width) * 8;
        MinimumSize = new Size(width, width * 3 / 5); // To-do：添加一个根据所有控件总高度调节最小高度的逻辑

        // Controls
        Controls.Add(mainPanel);
        Controls.Add(bottomPanel);
        bottomPanel.Controls.Add(btnSave);
        bottomPanel.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));

        // Events
        Shown += (sender, e) => RealPlugin.plug.InvokeNamedCallback("command", "/e <se.9>\n正在开启小队列表控件。\n如果没有看到，请检查后台窗口。");
        Shown += (sender, e) => mainPanel.AutoScrollPosition = new Point(0, 0);
        btnSave.Click += btnSave_Click;

        // PartyListPanel
        partyListPanel = new PartyListPanel(playerDescriptions);
        var group = new MyGroupBox { Text = " 小队顺序（拖拽调整）" };
        var panel = new GroupPanel();
        mainPanel.Controls.Add(panel);
        panel.Controls.Add(group);
        group.Controls.Add(partyListPanel);
    }

    public static Font CreateFont(float size, params string[] fontFamilies)
    {
        foreach (var fontFamily in fontFamilies)
        {
            try { return new Font(fontFamily, size); }
            catch (ArgumentException) { }
        }
        return SystemFonts.DefaultFont;
    }

    void btnSave_Click(object sender, EventArgs e)
    {
        partyListPanel.SaveToConfig();
        RealPlugin.plug.InvokeNamedCallback("command", "/e <se.10>\n已保存小队列表。");
        Close();
    }

    /// <summary> 读取配置，恢复表单布局，显示表单。</summary>
    public void Run()
    {
        partyListPanel.LoadFromConfig();
        ResumeLayout();
        ShowDialog();
        Dispose();
    }

    class BottomTableLayoutPanel : TableLayoutPanel
    {
        public BottomTableLayoutPanel() : base()
        {
            Dock = DockStyle.Bottom;
            RowCount = 1;
            ColumnCount = 1;
            ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));
        }
    }
    class MyGroupBox : GroupBox
    {
        public MyGroupBox() : base()
        {
            Dock = DockStyle.Top;
            AutoSize = true;
            AutoSizeMode = AutoSizeMode.GrowAndShrink;
            Margin = new Padding(20);
        }
    }
    class MyButton : Button
    {
        public MyButton() : base()
        {
            Anchor = AnchorStyles.None;
            AutoSize = true;
            Margin = new Padding(5);
            Padding = new Padding(5);
        }
    }
    class BackgroundPanel : Panel
    {
        public BackgroundPanel() : base()
        {
            AutoSize = true;
            AutoSizeMode = AutoSizeMode.GrowAndShrink;
            Dock = DockStyle.Fill;
            AutoScroll = true;
        }
    }
    class GroupPanel : Panel
    {
        public GroupPanel() : base()
        {
            AutoSize = true;
            AutoSizeMode = AutoSizeMode.GrowAndShrink;
            Dock = DockStyle.Top;
            Padding = new Padding(20, 20, 20, 0);
        }
    }

}

#endregion

Action.VariableExpression:
已设置自身职能：${?l: MT, ST, H1, H2, D1, D2, D3, D4 [0${v:myIdx}]}
如有误，可输入文本指令设置，大小写不限。
如：　/e mt　　/e h1　　/e d4

已設置自身職能：${?l: MT, ST, H1, H2, D1, D2, D3, D4 [0${v:myIdx}]}
如有誤，可輸入文本指令設置，大小写不限。
如：　/e mt　　/e h1　　/e d4

Action.NamedCallbackParam:
// 左侧箭头可以展开为多行模式
// 双斜线开始的行会当做为注释行，和空行一起被忽略
// 所有参数的顺序不重要，每行提供一个参数

// 采用日志中的坐标系，即正东 x+，正南 y+，正上 z+
// 角度从正北开始逆时针递增，如 北 西 南 东 分别为： ±π, -π/2, 0, π/2

// 必须参数：action，代表标点模式
// place 模式下允许你输入一组相对坐标
action: place

// 可选参数：scaleX scaleY scaleZ：坐标在各个方向上的缩放尺度，负数即为反向
// 未提供时的默认值：均为 1
scaleX: 2
scaleY: -2
// 如果尺度相同，可以使用 scale: 2 简化

// 可选参数 θ：旋转角度，可以理解为将这个相对坐标系的正北旋转至哪个绝对方向
// 未提供时的默认值：-π （将正北旋转至正北，即未旋转）
θ: π/2
// 也可以写作角度，如 θ: 90°

// 可选参数 center：中心坐标
// 下方演示的是使用触发器的实体查询功能获取并填入自身坐标
// 未提供时的默认值：0, 0, 0
center: ${_me.x}, ${_me.y}, ${_me.z}

// 提供的坐标首先经过缩放变换，其次经过平面旋转，最后平移至中心坐标

// 下方给出所有相对坐标：
A: 0, -4, 0
// z = 0 可以省略：
B: 4, -4
// 也可以使用极坐标模式：polar [r], [θ]：
C: polar 4, 90°
// 也可以结合两种模式，以平面直角坐标系下的坐标为中心进一步添加一个极坐标系的偏移：
D: 0, 4 polar 2, 30°
// 清除一个标点：
1: clear
// “清除”一个标点，实为移动到看不见的位置，使之下次出现时没有淡入动画：
2: fakeclear
// 没有提供的标点会被忽略，保持标点的原状态
// 3:... 4:...

// 三条 hyphen 连字符为分隔线，代表另起一条指令，所有指令依次执行
---

// circle 模式下通过给定的圆心和半径将所有点标记在圆周的等分位置
action: circle

// 必须参数：waymarks，从正北开始所有的标点
// 可以使用 _ 表示这个位置依旧需要等分，但是并不放置一个标点，如下面代表八等分圆但正南不标点
waymarks: ABCD_234

// 必须参数：r，半径
r: 5

// 必须参数：center 圆心坐标
center: ${_me.x}, ${_me.y}, ${_me.z}

// 可选参数：θ，与 place 中类似
θ: -π

// linear（连接两个点）和 arc（朝某个方向标记圆弧）指令还没写介绍，有兴趣可以看代码
