Action.ExecScriptExpression:
using System;
using System.Windows.Forms;
using Triggernometry;
using static Triggernometry.Interpreter;
void Main()
{var postNamazu = RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu");
if (postNamazu.pluginObj == null)
{MessageBox.Show(
"鲶鱼精邮差 PostNamazu 未安装或未启动。",
"空天姬",
MessageBoxButtons.OK,
MessageBoxIcon.Error
);
发送日志("空天姬超时");
return;
}
var current = $"目前安装的版本为：{postNamazu.FileVersion}";
var correct = $"所需的最低版本：${env:namazu}";
StaticHelpers.SetScalarVariable(false, "空天姬控制鲶鱼精版本I", $"{postNamazu.FileVersion}");
if (new Version(postNamazu.FileVersion) < new Version("${env:namazu}"))
{RealPlugin.plug.UpdatePostNamazu("${env:namazu}");
}
else
{StaticHelpers.SetScalarVariable(false, "空天姬控制鲶鱼精检查I", "1");
}}
void 发送日志(string text)
{var tri = new Trigger();
StaticHelpers.fakectx.trig = tri;
RealPlugin.plug.QueueAction(
StaticHelpers.fakectx,
StaticHelpers.fakectx.trig,
m: null,
new Triggernometry.Action
{ActionType = Triggernometry.Action.ActionTypeEnum.LogMessage.ToString(),
LogMessageText = $"{text}",
LogProcess = "true",
},
DateTime.Now,
releaseMutex: true
);}
Main();

