【测试用版本】你当前使用的是测试用的快照版本。不确保稳定。",""):${_triggerpath}}
【最近版本更新摘要】${env:摘要}

Action.ExecScriptExpression:
using System;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Threading;
using System.Windows.Forms;
using PostNamazu;
using PostNamazu.Common;
using GreyMagic;
using Triggernometry;
using Triggernometry.PluginBridges;
using System.Numerics;
bool initialized = false;
for (int i = 1; i <= 5; i++)
{try
{MissFisherExtension.Init();
initialized = true;
break;
}
catch
{MissFisherExtension.Log(RealPlugin.DebugLevelEnum.Warning, $"空天姬扩展初始化失败，正在重试第 {i} / 5 次……");
if (i == 5) throw;
}
Thread.Sleep(i * 1000);}
public static class MissFisherExtension
{private static readonly string Name = "MissFisherExtension";
public static PostNamazu.PostNamazu NamazuPlugin =>
(PostNamazu.PostNamazu)RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").pluginObj;
public static ExternalProcessMemory Memory => NamazuPlugin?.Memory;
public static SigScanner Scanner => NamazuPlugin?.SigScanner;
public static bool IsCN => !BridgeFFXIV.GetMyself().GetValue("name").ToString().Contains(" ");
private static PostNamazuUi _postNamazuUi;
public static PostNamazuUi PostNamazuUi
{get
{if (_postNamazuUi?.Parent as TabPage == null)
{_postNamazuUi = GetPostNamazuUi();
}
return _postNamazuUi;
}
}
public static bool IsReady => Triggernometry.Interpreter.StaticHelpers.XivProcess != null && Memory != null;
private static PostNamazuUi GetPostNamazuUi()
{TabPage tp = RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").TabPage;
return tp.Controls.OfType<PostNamazuUi>().FirstOrDefault();
}
public static void OnXivProcUpdate()
{NamazuUiLog($"空天姬初始化：开始扫描内存~");
ScanSignatures();
}
public static void RegisterCallback(
string callBackName,
Action<string> callBackAction,
IntPtr? ptr = null
)
{if (ptr == IntPtr.Zero || ptr == null)
{Log(RealPlugin.DebugLevelEnum.Error, $"注册回调 {callBackName} 失败：指针无效 (ptr == IntPtr.Zero)");
return;
}
Triggernometry.RealPlugin.plug.RegisterNamedCallback(
callBackName,
new Action<object, string>((_, cmd) => callBackAction(cmd)),
null,
registrant: Name
);
}
public static void Init()
{OnXivProcUpdate();
Triggernometry.Interpreter.StaticHelpers.RegisterXivProcessUpdatedAction(
Name,
new System.Action(OnXivProcUpdate)
);

RegisterCallback("KTJChangeBaitAuto", FishingHelper.ChangeBaitAuto, ExecuteCommandPtr);

RegisterCallback("KTJChangeBaitById", FishingHelper.ChangeBaitById, ExecuteCommandPtr);

RegisterCallback("KTJChangeSwimBaitByIndex", FishingHelper.ChangeSwimBaitByIndex, ExecuteCommandPtr);

RegisterCallback("KTJGetCurrentBait", FishingHelper.GetCurrentBait, PlayerStatePtr);

RegisterCallback("KTJGetCurrentWeather", FishingHelper.GetCurrentWeather, WeatherManagerPtr);

RegisterCallback("KTJUseItem", FishingHelper.UseItem, UseActionPtr);

RegisterCallback("KTJObtainOceanFishing", FishingHelper.ObtainOceanFishing, GetInstanceContentDirectorPtr);
if (FishingHelper.CanChangeBait())
{Echo($"已激活：空天姬内存组件\r\n{Name} v0.09");
}
else
{Echo($"");
Echo($"{Name} 初始化失败！");
}
}
public static void CheckBeforeExecution()
{if (!IsReady)
throw new Exception("KTJCheckBeforeExecution 没有对应的 FFXIV 进程");
}
public static IntPtr ExecuteCommandPtr;
public static IntPtr PlayerStatePtr;
public static IntPtr WeatherManagerPtr;
public static IntPtr ActionManagerPtr;
public static IntPtr UseActionPtr;
public static IntPtr EventFrameworkPtr;
public static IntPtr GetInstanceContentDirectorPtr;
public static void ScanSignatures()
{ExecuteCommandPtr = ScanSignatureWithFallback(
nameof(ExecuteCommandPtr),
"E8 ?? ?? ?? ?? 48 83 C4 ?? C3 CC CC CC CC CC CC CC CC CC CC CC CC 48 83 EC ?? 45 0F B6 C0",
"E8 ? ? ? ? 48 8B 06 48 8B CE FF 50 ? E9 ? ? ? ? 49 8B CC"
);
var PlayerStatePtrPtr = Scanner.ScanText(
"48 8D 0D ?? ?? ?? ?? E8 ?? ?? ?? ?? 84 C0 75 06 F6 43 18 02",
nameof(PlayerStatePtr)
);
PlayerStatePtr = PlayerStatePtrPtr + 7 + Memory.Read<int>(PlayerStatePtrPtr + 3);
var weatherManagerPtrPtr = Scanner.ScanText("48 8D 0D ?? ?? ?? ?? 44 0F B7 45", nameof(WeatherManagerPtr));
WeatherManagerPtr = weatherManagerPtrPtr + 7 + Memory.Read<int>(weatherManagerPtrPtr + 3);
var actionManagerPtrPtr = Scanner.ScanText("48 8D 0D ?? ?? ?? ?? F3 0F 10 13", nameof(ActionManagerPtr));
ActionManagerPtr = actionManagerPtrPtr + 7 + Memory.Read<int>(actionManagerPtrPtr + 3);
UseActionPtr = Scanner.ScanText("E8 ?? ?? ?? ?? 41 89 9E ?? ?? ?? ?? EB 0B", nameof(UseActionPtr));
var eventFrameworkPtrPtrPtr = ScanSignatureWithFallback(
nameof(EventFrameworkPtr),
"48 8B 35 ?? ?? ?? ?? 0F B6 EA 4C 8B F1",
"4C 39 2D ?? ?? ?? ?? 74 14" 
);
var eventFrameworkPtrPtr = eventFrameworkPtrPtrPtr + 7 + Memory.Read<int>(eventFrameworkPtrPtrPtr + 3);
EventFrameworkPtr = Memory.Read<IntPtr>(eventFrameworkPtrPtr);
GetInstanceContentDirectorPtr = ScanSignatureWithFallback(nameof(GetInstanceContentDirectorPtr), "E8 ?? ?? ?? ?? 0F B6 98");
}
private static IntPtr ScanSignatureWithFallback(string name, params string[] patterns)
{if (patterns == null || patterns.Length == 0)
{Log(RealPlugin.DebugLevelEnum.Error, $"ScanSignatureWithFallback: {name} 未提供任何 Sig");
return IntPtr.Zero;
}
foreach (var pattern in patterns)
{try
{IntPtr ptr = Scanner.ScanText(pattern, name);
if (ptr != IntPtr.Zero)
{return ptr;
}
}
catch (Exception ex) {}
}
Log(RealPlugin.DebugLevelEnum.Warning,  $"ScanSignatureWithFallback: `{name}` 所有备用Sig均扫描失败/返回空指针。");
return IntPtr.Zero;
}
public static class FishingHelper
{public static bool CanChangeBait()
{return !(ExecuteCommandPtr == null || ExecuteCommandPtr == IntPtr.Zero);
}
public static void ChangeBaitAuto(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var swimIndex) && swimIndex is >= 0 and < 3)
{ChangeSwimBaitByIndex(cmd);
}
else
{ChangeBaitById(cmd);
}
}
public static void ChangeBaitById(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var baitId))
{NamazuUiLog($"KTJChangeBait: baitId = {baitId}");
Memory.CallInjected64<IntPtr>(ExecuteCommandPtr, 701, 4, (int)baitId, 0, 0);
}
}
public static void ChangeSwimBaitByIndex(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var swimIndex) && swimIndex is >= 0 and < 3)
{NamazuUiLog($"KTJChangeSwimBait: swimIndex = {swimIndex}");
Memory.CallInjected64<IntPtr>(ExecuteCommandPtr, 701, 25, (int)swimIndex, 0, 0);
}
}
public static void GetCurrentBait(string cmd)
{CheckBeforeExecution();
var currentBait = Memory.Read<uint>(PlayerStatePtr + 0x4A4);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制钓饵",
currentBait
);
}
public static void GetCurrentWeather(string cmd)
{CheckBeforeExecution();
var current_weather = Memory.Read<byte>(WeatherManagerPtr + 0x64);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬天气ID",
current_weather
);
}
public static void UseItem(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var itemId))
{NamazuUiLog($"KTJUseItem: itemId = {itemId}");
Memory.CallInjected64<char>(UseActionPtr, (long)ActionManagerPtr, 2, itemId, 0xE000_0000, 0xFFFF, 0, 0, 0);
}
}
public static void ObtainOceanFishing(string cmd)
{CheckBeforeExecution();
var directorPtr = Memory.CallInjected64<IntPtr>(GetInstanceContentDirectorPtr, EventFrameworkPtr);
if (directorPtr == IntPtr.Zero || directorPtr == null)
{MissFisherExtension.Log(RealPlugin.DebugLevelEnum.Warning, $"{Name}: ContentDirector获取失败，现在真在副本中吗？");
return;
}
var directorType1 = Memory.Read<ushort>(directorPtr + 0xDCE); 
var directorType2 = Memory.Read<ushort>(directorPtr + 0xE1E); 
if (directorType1 != 0x10 && directorType2 != 0x10) {MissFisherExtension.Log(RealPlugin.DebugLevelEnum.Warning, $"{Name}: 现在真在海钓吗？({directorType1}|{directorType2})");
return;
}

var status = Memory.Read<uint>(directorPtr + 0x1DBC);
var currentRoute = Memory.Read<uint>(directorPtr + 0x1DB8);
var currentZone = Memory.Read<uint>(directorPtr + 0x1DC0);
var spectralCurrentActive = Memory.Read<bool>(directorPtr + 0x1DD0);
var weatherId = Memory.Read<uint>(directorPtr + 0x1DCC);
var contentTimeLeft = Memory.Read<float>(directorPtr + 0xCF0);
var timeOffset= Memory.Read<uint>(directorPtr + 0x1DC8);
var timeLeft = contentTimeLeft - timeOffset; 
var entsAt = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() + (int)timeLeft * 1000;
var playerAllPoint= Memory.Read<ushort>(directorPtr + 0x220C + 0x4);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓状态", status); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓当前路线", currentRoute); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓当前海域", currentZone); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓是否幻海中", spectralCurrentActive);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓当前天气ID", weatherId);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓当前海域结束时间", entsAt); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬控制海钓结束时总分数", playerAllPoint); 
}
}
public static void Echo(string msg) => RealPlugin.plug.InvokeNamedCallback("command", $"/e {msg}");
public static void Log(RealPlugin.DebugLevelEnum level, string msg) => Interpreter.StaticHelpers.Log(level, msg);
public static void NamazuUiLog(string msg) => PostNamazuUi?.Log($"{Name}: {msg}");}

