void 初始化预设关联控件内容()
{ 
字典 = StaticHelpers.GetDictVariable(true, "空天姬预设");

说明 = 字典.GetValue("说明")?.ToString() ?? "";
赞助 = 字典.GetValue("赞助")?.ToString() ?? "";

预设 = 字典
.Values.Keys.Where(item =>
!string.IsNullOrEmpty(item) && !char.IsDigit(item[0]) && !item.StartsWith("空天姬")
)
.ToList();
new ToolTip().SetToolTip(输入鱼名, 说明.Replace("\\r\\n", Environment.NewLine));
输入鸣谢.Text = 赞助.Replace("\\r\\n", Environment.NewLine);}
void 设置控件关系()
{ 
窗口.Controls.Add(表格);
表格.ColumnCount = 4; 
表格.RowCount = 7; 

Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
float dpiX = graphics.DpiX;
float dpiY = graphics.DpiY;
graphics.Dispose();
float scaleFactor = dpiY / 96f;
int 行高像素 = (int)(38 * scaleFactor);
int 列宽像素 = (int)(76 * scaleFactor);
for (int i = 0; i < 表格.RowCount; i++)
{表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
}
for (int j = 0; j < 表格.ColumnCount; j++)
{表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
}

表格.Controls.Add(按钮海钓, 0, 3);
表格.SetRowSpan(按钮海钓, 2);
表格.SetColumnSpan(按钮海钓, 2);
表格.Controls.Add(下拉海钓, 2, 3);
表格.SetColumnSpan(下拉海钓, 2);
表格.Controls.Add(按钮配置, 3, 1);
表格.Controls.Add(按钮同步, 3, 0);
表格.Controls.Add(按钮航线, 3, 2);
表格.Controls.Add(输入鸣谢, 0, 0);
表格.SetColumnSpan(输入鸣谢, 3);
表格.SetRowSpan(输入鸣谢, 3);
表格.Controls.Add(输入鱼名, 2, 4);
表格.SetColumnSpan(输入鱼名, 2);
表格.Controls.Add(文本诗句, 0, 5);
表格.SetColumnSpan(文本诗句, 4);
表格.SetRowSpan(文本诗句, 2);}
void 设置控件样式()
{ 
foreach (Control c in 表格.Controls)
{ 
c.Font = 字体;
c.ForeColor = Color.White;
c.AutoSize = true;
c.Dock = DockStyle.Fill;
if (c is Label l)
{l.Font = 加粗;
l.ForeColor = Color.FromArgb(196, 172, 123); 
l.TextAlign = ContentAlignment.MiddleCenter;
}
文本诗句.Font = 楷体;
文本诗句.TextAlign = ContentAlignment.MiddleCenter;
if (c is Button b)
{b.Font = 加粗;
b.FlatStyle = FlatStyle.Flat;
b.BackColor = Color.FromArgb(50, 81, 155);
b.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68);
}
按钮配置.BackColor = 红色;
按钮配置.ForeColor = 黄色;
按钮同步.BackColor = 蓝色;
按钮同步.ForeColor = 黄色;
按钮航线.BackColor = 灰色;
按钮航线.ForeColor = 深灰;
if (c is TextBox t)
{t.Font = 加粗;
t.BorderStyle = BorderStyle.FixedSingle;
t.BackColor = Color.FromArgb(49, 67, 103);
}
输入鸣谢.Font = 小号加粗;
输入鸣谢.ForeColor = Color.FromArgb(196, 172, 123);
输入鸣谢.BackColor = Color.FromArgb(21, 35, 71);
输入鱼名.Font = 字体;
输入鱼名.ForeColor = 灰色;
if (c is CheckBox 勾选框)
{勾选框.ForeColor = Color.FromArgb(234, 222, 195);
}
if (c is ComboBox cbb)
{cbb.DropDownStyle = ComboBoxStyle.DropDownList;
cbb.DrawMode = DrawMode.OwnerDrawFixed;
cbb.SelectedIndex = 0;
cbb.BackColor = Color.FromArgb(41, 60, 107); 
cbb.ForeColor = Color.FromArgb(234, 222, 195);
添加下拉前缀(cbb, "▶"); 
cbb.DrawItem += new DrawItemEventHandler(绘制下拉框);
}
}
StaticHelpers.SetScalarVariable(false, "空天姬控制窗口I", "1");
RealPlugin.plug.InvokeNamedCallback("command", "/e 窗口加载成功！<se.9>");}
void 绘制下拉框(object sender, DrawItemEventArgs e)
{e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
StringFormat stringFormat = new StringFormat
{Alignment = StringAlignment.Near,
LineAlignment = StringAlignment.Center,
};
e.Graphics.DrawString(
((ComboBox)sender).Items[e.Index].ToString(),
e.Font,
new SolidBrush(e.ForeColor),
e.Bounds.X,
e.Bounds.Y
);
using (Pen borderPen = new Pen(Color.FromArgb(41, 60, 107)))
{e.Graphics.DrawRectangle(
borderPen,
e.Bounds.X,
e.Bounds.Y,
e.Bounds.Width - 1,
e.Bounds.Height - 1
);
}}
void 添加下拉前缀(ComboBox comboBox, string prefix)
{int selectedIndex = comboBox.SelectedIndex;
List<string> newItems = new List<string>();
foreach (var item in comboBox.Items)
{if (item is string strItem)
{newItems.Add($"{prefix}{strItem}");
}
}
comboBox.Items.Clear();
comboBox.Items.AddRange(newItems.ToArray());
if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
{comboBox.SelectedIndex = selectedIndex;
}
else
{comboBox.SelectedIndex = 0;
}}
void 更新按钮文本(object sender, EventArgs e)
{var a = StaticHelpers.GetScalarVariable(false, "空天姬参数") ?? "";
var o = StaticHelpers.GetScalarVariable(false, "空天姬控制海钓模式") ?? "";
if (string.IsNullOrEmpty(a) || string.IsNullOrEmpty(o))
{按钮海钓.ForeColor = Color.White;
string 海钓选项 = 下拉海钓.SelectedItem.ToString();
if (海钓选项.StartsWith("▶"))
{海钓选项 = 海钓选项.Substring(1);
}
按钮海钓.Font = 加粗;
按钮海钓.Text = "启动" + 海钓选项;
设置提示(按钮海钓, $"启动{海钓选项}" + "\r\n右键切换悬浮窗状态");
}}
void 输入海钓(object sender, MouseEventArgs e)
{switch (e.Button)
{case MouseButtons.Left:
string 海钓选项 = 下拉海钓.SelectedItem.ToString();
if (海钓选项.StartsWith("▶"))
{海钓选项 = 海钓选项.Substring(1);
}
发送日志($"空天姬海钓启动：{海钓选项}");
break;
case MouseButtons.Middle:
if (!string.IsNullOrEmpty(StaticHelpers.GetScalarVariable(false, "空天姬参数")))
{发送日志("空天姬暂停");
延时更新("已 发 送 暂 停 指 令", 1500).ConfigureAwait(false);
}
else
{延时更新("空 天 姬 还 未 启 动", 3000).ConfigureAwait(false);
}
break;
case MouseButtons.Right:
发送日志("空天姬切换悬浮窗");
延时更新("已 发 送 切 换 指 令", 1500).ConfigureAwait(false);
break;
}}
void 输入鱼名_KeyDown(object sender, KeyEventArgs e)
{if (e.KeyCode == Keys.Enter)
{ 
MouseEventArgs leftDoubleClickEventArgs = new MouseEventArgs(MouseButtons.Left, 2, 0, 0, 0);
输入预设(sender, leftDoubleClickEventArgs);
}}
void 更新颜色(object sender, EventArgs e)
{if (!输入鱼名.Text.StartsWith("输"))
{if (预设.Contains(输入鱼名.Text))
{输入鱼名.Font = 字体;
输入鱼名.ForeColor = Color.FromArgb(196, 172, 123);
输入鱼名.BackColor = Color.FromArgb(21, 35, 71); 
if (文本诗句.Text != "双 击 或 回 车\r\n启 动 预 设\r\n中 键 发 送 预 览")
{延时更新("双 击 或 回 车\r\n启 动 预 设\r\n中 键 发 送 预 览", 10000)
.ConfigureAwait(false);
}
}
else
{输入鱼名.Font = 字体;
输入鱼名.ForeColor = Color.FromArgb(196, 172, 123);
输入鱼名.BackColor = Color.FromArgb(49, 67, 103); 
}
}
else
{输入鱼名.Font = 字体;
输入鱼名.BackColor = Color.FromArgb(49, 67, 103); 
输入鱼名.ForeColor = 灰色; 
}}
void 提示(object sender, EventArgs e)
{if (输入鱼名.Text == "")
{输入鱼名.Text = 输入提示;
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}}
void 输入预设(object sender, MouseEventArgs e)
{ 
if (
(e.Button == MouseButtons.Left && e.Clicks == 2)
|| (e.Button == MouseButtons.Middle && e.Clicks == 1)
)
{if (!输入鱼名.Text.StartsWith("输") && !string.IsNullOrEmpty(输入鱼名.Text))
{if (预设.Contains(输入鱼名.Text))
{if (e.Button == MouseButtons.Left && e.Clicks == 2)
{RealPlugin.plug.InvokeNamedCallback(
"command",
默语 + "空天姬预设 " + 输入鱼名.Text
);
延时更新("已 发 送 至 默 语 频 道", 3000).ConfigureAwait(false);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
else if (e.Button == MouseButtons.Middle && e.Clicks == 1)
{RealPlugin.plug.InvokeNamedCallback(
"command",
默语 + "空天姬预设 查看 " + 输入鱼名.Text
);
延时更新("已 发 送 至 默 语 频 道", 3000).ConfigureAwait(false);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
}
else
{延时更新("未 支 持 此 预 设\r\n反 馈 信 息 已 发 至 默 语", 5000)
.ConfigureAwait(false);
RealPlugin.plug.InvokeNamedCallback(
"command",
默语 + "空天姬预设 查看 " + 输入鱼名.Text
);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
}
else
{延时更新("请 输 入 鱼 名", 1500).ConfigureAwait(false);
}
}
else if (e.Button == MouseButtons.Left && e.Clicks == 1)
{if (输入鱼名.Text.StartsWith("输"))
{输入鱼名.Text = "";
}
}}
void 输入航线(object sender, EventArgs e)
{发送日志("空天姬查询航线");}
async Task 延时更新(string 文本内容, int 延迟毫秒数)
{文本诗句.Text = 文本内容;
await Task.Delay(延迟毫秒数);
if (!所有诗句.Contains(文本诗句.Text))
{更新诗句();
}}
async Task 刷新启动按钮()
{while (true)
{var a = StaticHelpers.GetScalarVariable(false, "空天姬参数") ?? "";
var o = StaticHelpers.GetScalarVariable(false, "空天姬控制海钓模式") ?? "";
var p = StaticHelpers.GetScalarVariable(false, "空天姬控制暂停") ?? "";

if (a != _lastA || o != _lastO || (p == "1") != _isPaused)
{_lastA = a;
_lastO = o;
_isPaused = p == "1";
if (a != "" || o != "")
{按钮海钓.FlatAppearance.BorderColor = _isPaused
? Color.FromArgb(255, 238, 173)
: Color.FromArgb(215, 210, 236);
按钮海钓.ForeColor = _isPaused ? Color.FromArgb(255, 238, 173) : Color.White;
按钮海钓.Font = 小号加粗;
按钮海钓.Text =
(_isPaused ? "//// 暂停中 ////" : "▶ ▶运行中 ▶ ▶")
+ "\r\n右键切换悬浮窗状态\r\n左键启动 中键"
+ (_isPaused ? "恢复" : "暂停");
设置提示(按钮海钓, _isPaused ? "自动动作暂停中" : "空天姬正在运行");
}
else
{按钮海钓.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68);
更新按钮文本(窗口, EventArgs.Empty);
}
}
await Task.Delay(1000);
}}
void 设置提示(Control c, string t)
{if (c.Tag == null || !(c.Tag is ToolTip))
{c.Tag = new ToolTip();
}
var 悬浮提示 = (ToolTip)c.Tag;
悬浮提示.SetToolTip(c, t);}
void 复制爱发电(object sender, EventArgs e)
{输入鸣谢.SelectionLength = 0;
延时更新复制("已 复 制 爱 发 电 链 接", 3000, "https://afdian.com/a/bcloli")
.ConfigureAwait(false);}
async Task 延时更新复制(string 文本内容, int 延迟毫秒数, string 链接)
{await Task.Run(() =>
{Triggernometry.Action.ClipboardSetText(链接);
});
文本诗句.Text = 文本内容;
await Task.Delay(延迟毫秒数);
if (!所有诗句.Contains(文本诗句.Text))
{更新诗句();
}}
void 输入同步(object sender, EventArgs e)
{发送日志("空天姬更新预设");
初始化预设关联控件内容();
延时更新预设关联控件内容();}
async Task 延时更新预设关联控件内容()
{for (int i = 0; i < 6; i++)
{await Task.Delay(5000);
初始化预设关联控件内容();
}}
void 输入配置(object sender, EventArgs e)
{发送日志("空天姬打开配置");}
void 更新诗句()
{List<string> 可选诗句 = new List<string>(所有诗句);
foreach (var 诗 in 已显示诗句)
{可选诗句.Remove(诗);
}
if (可选诗句.Count > 0)
{int 随机索引 = 随机数.Next(可选诗句.Count);
string 新诗句 = 可选诗句[随机索引];
已显示诗句.Add(新诗句);
文本诗句.Text = 新诗句;
}
else
{已显示诗句.Clear();
更新诗句();
}}
void 发送日志(string text)
{var tri = new Trigger();
StaticHelpers.fakectx.trig = tri;
RealPlugin.plug.QueueAction(
StaticHelpers.fakectx,
StaticHelpers.fakectx.trig,
m: null,
new Triggernometry.Action
{ActionType = Triggernometry.Action.ActionTypeEnum.LogMessage.ToString(),
LogMessageText = $"{text}",
LogProcess = "true",
},
DateTime.Now,
releaseMutex: true
);}

Action.NamedCallbackParam:
/e [空天姬 MissFisher] <se.9>
