Action.ExecScriptExpression:
using System;
using System.Collections.Generic;
using System.Linq;
using System.Numerics;
using System.Text;
using System.Threading.Tasks;
using Triggernometry.FFXIV;
using Triggernometry.PluginBridges.BridgeNamazu;
using Triggernometry.PluginBridges.BridgeNamazu.Modules;
using Triggernometry.Utilities;
using Triggernometry.Utilities.Math;
using static Triggernometry.Utilities.DataStringHelper;

Triggernometry.RealPlugin.plug.RegisterNamedCallback("M6s_AutoFly", 
    new Action<object, string>(M6sAutoFly.AutoFly), null, false, "M6s宝宝椅");
Triggernometry.RealPlugin.plug.RegisterNamedCallback("M6s_AutoFlyLava", 
    new Action<object, string>(M6sAutoFly.AutoFlyLava), null, false, "M6s宝宝椅");

public static class M6sAutoFly
{

    const bool DEBUG = false;//true;

    const double FLYRANGE = 34.0;
    static Entity GetMyself() => Entity.GetMyself();
    static void Log(string msg) => Triggernometry.RealPlugin.plug.InvokeNamedCallback("command", $"/e \n{msg}");
    static void Warning(string msg) => Triggernometry.Interpreter.StaticHelpers.Log(Triggernometry.RealPlugin.DebugLevelEnum.Warning, msg);
    static void Error(string msg) => Triggernometry.Interpreter.StaticHelpers.Log(Triggernometry.RealPlugin.DebugLevelEnum.Error, msg);
    static void CheckAndSetHeading(IntPtr address, float prevθ, float newθ)
    {
        if (Math.Abs(Normalize(prevθ - newθ)) > 0.001)
        {
            var prevDeg = prevθ * 180 / Math.PI;
            var newDeg = newθ * 180 / Math.PI;
            BridgeNamazu.GetModule<EntityModule>().SetHeading(address, newθ);
            Log($"[面向] {prevDeg:F1}° => {newDeg:F1}°");
        }
    }

    public static void AutoFly(object _, string rawDirN4)
    { 
        int dirN4 = rawDirN4.FromDataString<int>();
        Task.Run(async () => {
            for (int i = 0; i < 10; i++)
            {
                if (i > 0) await Task.Delay(200);
                try
                {
                    _AutoFly(dirN4);
                }
                catch (IgnoredException iex) 
                {
                    if (DEBUG) Triggernometry.RealPlugin.plug.InvokeNamedCallback("AdvWm", "clear");
                    Warning($"[M6sAutoFly] {iex.Message}");
                }
                catch (Exception ex)
                {
                    Error($"[M6sAutoFly] 出现错误：{ex}");
                }
            }
        });
    }

    static void _AutoFly(int dirN4)
    {
        var me = GetMyself();
        var prevθ = me.Heading;
        var myX = me.PosX;
        var myY = me.PosY;
        dirN4 = (dirN4 + 4) % 4;
        var tgtX = dirN4 <= 1 ? 80 : 120;
        var tgtY = dirN4 == 0 || dirN4 == 3 ? 80 : 120;
        var newθ = (float)Math.Atan2(tgtX - myX, tgtY - myY);
        CheckAndSetHeading(me.Address, prevθ, newθ);
    }

    public static void AutoFlyLava(object _, string data)
    {
        (int dirN3, int direction) = ParseArgs<int, int>(data);
        Task.Run(async () => {
            for (int i = 0; i < 10; i++)
            {
                if (i > 0) await Task.Delay(200);
                try
                {
                    _AutoFlyLava(dirN3, direction);
                }
                catch (IgnoredException iex)
                {
                    if (DEBUG) Triggernometry.RealPlugin.plug.InvokeNamedCallback("AdvWm", "clear");
                    Warning($"[M6sAutoFly] {iex.Message}");
                }
                catch (Exception ex)
                {
                    Error($"[M6sAutoFly] 出现错误：{ex}");
                }
            }
        });
    }

    static void _AutoFlyLava(int dirN3, int direction)
    {
        var me = GetMyself();
        var prevθ = me.Heading;
        var myX = me.PosX;
        var myY = me.PosY;
        var myPos = new Vector2((float)myX, (float)myY);
        Vector2 edge1, edge2;

        if (dirN3 == 0 && direction == 1) // 左上 → 右上
        {
            if (myX < 120 - FLYRANGE) // 左边缘
            {
                edge1 = 上边缘交点(myX, myY).Item2;
                edge2 = 右上熔岩交点(myX, myY).Item2;
            }
            else if (myY <= 93) // 中心偏上
            {
                edge1 = 右边缘交点(myX, myY).Item2;
                edge2 = 右上熔岩交点(myX, myY).Item2;
            }
            else  // 中心偏下
            {
                edge1 = 上边缘交点(myX, myY).Item2;
                edge2 = 右边缘交点(myX, myY).Item1;
            }
        }
        else if (dirN3 == 2 && direction == -1) // 右上 → 左上
        {
            if (myX > 80 + FLYRANGE) // 右边缘
            {
                edge1 = 左上熔岩交点(myX, myY).Item1;
                edge2 = 上边缘交点(myX, myY).Item1;
            }
            else if (myY <= 93) // 中心偏上
            {
                edge1 = 左上熔岩交点(myX, myY).Item1;
                edge2 = 左边缘交点(myX, myY).Item2;
            }
            else  // 中心偏下
            {
                edge1 = 左边缘交点(myX, myY).Item1;
                edge2 = 上边缘交点(myX, myY).Item1;
            }
        }
        else if (dirN3 == 0 && direction == -1) // 左上 → 下
        {
            if (Vector2.Distance(myPos, edge左下外) > FLYRANGE) // 上边缘
            {
                edge1 = 右下熔岩交点(myX, myY).Item2;
                edge2 = 左下熔岩交点(myX, myY).Item1;
            }
            else if (myY < 120 - FLYRANGE) // 上边缘稍近
            {
                edge1 = 右下熔岩交点(myX, myY).Item2;
                edge2 = 左边缘交点(myX, myY).Item2;
            }
            else if (Vector2.Distance(myPos, edge右下外) <= FLYRANGE) // 中心
            {
                edge1 = 右边缘交点(myX, myY).Item2;
                edge2 = 下边缘交点(myX, myY).Item2;
            }
            else // 左侧边缘大部分区域
            {
                edge1 = 右下熔岩交点(myX, myY).Item2;
                edge2 = 下边缘交点(myX, myY).Item2;
            }
        }
        else if (dirN3 == 2 && direction == 1) // 右上 → 下
        {
            if (Vector2.Distance(myPos, edge右下外) > FLYRANGE) // 上边缘
            {
                edge1 = 右下熔岩交点(myX, myY).Item2;
                edge2 = 左下熔岩交点(myX, myY).Item1;
            }
            else if (myY < 120 - FLYRANGE) // 上边缘稍近
            {
                edge1 = 右边缘交点(myX, myY).Item2;
                edge2 = 左下熔岩交点(myX, myY).Item1;
            }
            else if (Vector2.Distance(myPos, edge左下外) <= FLYRANGE) // 中心
            {
                edge1 = 下边缘交点(myX, myY).Item1;
                edge2 = 左边缘交点(myX, myY).Item2;
            }
            else // 右侧边缘大部分区域
            {
                edge1 = 下边缘交点(myX, myY).Item1;
                edge2 = 左下熔岩交点(myX, myY).Item1;
            }
        }
        else if (dirN3 == 1 && direction == 1) // 下 → 左上
        {
            if (Vector2.Distance(myPos, edge左上外) >= FLYRANGE) // 右下角落
            {
                edge1 = 左上熔岩交点(myX, myY).Item1;
                edge2 = 上左熔岩交点(myX, myY).Item1;
            }
            else if (myY > 80 + FLYRANGE)
            {
                edge1 = 左边缘交点(myX, myY).Item1;
                edge2 = 上左熔岩交点(myX, myY).Item1;
            }
            else if (myX > 90) // 中心偏右
            {
                edge1 = 左边缘交点(myX, myY).Item1;
                edge2 = 上边缘交点(myX, myY).Item1;
            }
            else // 中心偏左
            {
                edge1 = 上边缘交点(myX, myY).Item2;
                edge2 = 上左熔岩交点(myX, myY).Item1;
            }
        }
        else if (dirN3 == 1 && direction == -1) // 下 → 右上
        {
            if (Vector2.Distance(myPos, edge上右外) <= FLYRANGE) // 中心
            {
                edge1 = 上边缘交点(myX, myY).Item2;
                edge2 = 右边缘交点(myX, myY).Item1;
            }
            else if (Vector2.Distance(myPos, edge右上外) <= FLYRANGE) // 右下大部分区域
            {
                edge1 = 上右熔岩交点(myX, myY).Item1;
                edge2 = 右边缘交点(myX, myY).Item1;
            }
            else // 左下角落
            {
                edge1 = 上右熔岩交点(myX, myY).Item1;
                edge2 = 右上熔岩交点(myX, myY).Item2;
            }
        }
        else if (dirN3 == 1 && direction == 0) // 左下 ↔ 右下
        {
            if (me.PosX < 120 - FLYRANGE) // 左边缘
            {
                edge1 = 下边缘交点(myX, myY).Item2;
                edge2 = 右下熔岩交点(myX, myY).Item2;
            }
            else if (me.PosX > 80 + FLYRANGE) // 右边缘
            {
                edge1 = 下边缘交点(myX, myY).Item1;
                edge2 = 左下熔岩交点(myX, myY).Item1;
            }
            else if (myX < 100) // 左侧靠中心（实际只有很小的一部分区域有效 下同）
            {
                edge1 = 右边缘交点(myX, myY).Item2;
                edge2 = 下边缘交点(myX, myY).Item2;
            }
            else // 右侧靠中心
            {
                edge1 = 左边缘交点(myX, myY).Item2;
                edge2 = 下边缘交点(myX, myY).Item1;
            }
        }
        else throw new IgnoredException($"无效的位置：dirN3 = {dirN3}, direction = {direction}");

        var newθ = (float)BestAngle(myPos, edge1, edge2);
        var newPos = new Vector2(
            myPos.X + (float)(FLYRANGE * Math.Sin(newθ)),
            myPos.Y + (float)(FLYRANGE * Math.Cos(newθ))
        );
        if (Math.Abs(newPos.X - 100) > 20.01 || Math.Abs(newPos.Y - 100) > 20.01)
        {
            throw new IgnoredException($"无法找到安全的方向：myPos=({myX}, {myY}), newθ={newθ}, newPos=({newPos.X}, {newPos.Y})");
        }
        CheckAndSetHeading(me.Address, prevθ, newθ);
        // debug
        if (DEBUG) 
        {
            var pos = GetMyself().Pos;
            Triggernometry.RealPlugin.plug.InvokeNamedCallback("AdvWm", $@"
Action: Linear
O: {pos.X:F3}, {pos.Y:F3}, {pos.Z:F3}
θ: {newθ}
Start: 0, 0
End: 0, -34
Waymarks: 43DCBA
---
1: {edge1.X:F3}, {edge1.Y:F3}
2: {edge2.X:F3}, {edge2.Y:F3}
");
        }
    }

    static Vector2 edge左上外 = new Vector2(80, 105);
    static Vector2 edge上左外 = new Vector2(99.5f, 80);
    static Vector2 edge左上内 = new Vector2(95.5f, 97.3f);

    static Vector2 edge右上外 = new Vector2(120, 109);
    static Vector2 edge上右外 = new Vector2(107.5f, 80);
    static Vector2 edge右上内 = new Vector2(102.6f, 100.3f);

    static Vector2 edge左下外 = new Vector2(80, 115);
    static Vector2 edge右下外 = new Vector2(120, 115.6f);
    static Vector2 edge下内 = new Vector2(97, 105);

    static (Vector2, Vector2) 上边缘交点(double myX, double myY) 
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, 0, 1, -80, p => p.X)
            ?? throw new IgnoredException("未找到与上边缘交点");
    static (Vector2, Vector2) 下边缘交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, 0, 1, -120, p => p.X)
            ?? throw new IgnoredException("未找到与下边缘交点");
    static (Vector2, Vector2) 左边缘交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, 1, 0, -80, p => p.Y)
            ?? throw new IgnoredException("未找到与左边缘交点");
    static (Vector2, Vector2) 右边缘交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, 1, 0, -120, p => p.Y)
            ?? throw new IgnoredException("未找到与右边缘交点");
    static (Vector2, Vector2) 左上熔岩交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, edge左上外, edge左上内, p => p.X)
            ?? throw new IgnoredException("未找到与左上熔岩交点");
    static (Vector2, Vector2) 右上熔岩交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, edge右上外, edge右上内, p => p.X)
            ?? throw new IgnoredException("未找到与右上熔岩交点");
    static (Vector2, Vector2) 上左熔岩交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, edge上左外, edge左上内, p => p.Y)
            ?? throw new IgnoredException("未找到与上左熔岩交点");
    static (Vector2, Vector2) 上右熔岩交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, edge上右外, edge右上内, p => p.Y)
            ?? throw new IgnoredException("未找到与上右熔岩交点");
    static (Vector2, Vector2) 左下熔岩交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, edge左下外, edge下内, p => p.X)
            ?? throw new IgnoredException("未找到与左下熔岩交点");
    static (Vector2, Vector2) 右下熔岩交点(double myX, double myY)
        => EquationHelper.CircleLineIntersections(myX, myY, FLYRANGE, edge右下外, edge下内, p => p.X)
            ?? throw new IgnoredException("未找到与右下熔岩交点");

    static double BestAngle(Vector2 src, Vector2 edge1, Vector2 edge2)
    {
        var θ1 = Math.Atan2(edge1.X - src.X, edge1.Y - src.Y);
        var θ2 = Math.Atan2(edge2.X - src.X, edge2.Y - src.Y);
        double δ = Normalize(θ2 - θ1);
        double mid = θ1 + δ / 2;
        return Normalize(mid);
    }
    static double Normalize(double angle)
    {
        angle = (angle + Math.PI) % (2 * Math.PI);
        if (angle <= 0) angle += 2 * Math.PI;
        return angle - Math.PI;
    }

    class IgnoredException : Exception { 
        public IgnoredException(string message) : base(message) { }
    }

}
