using System;
using System.Drawing;
using System.Linq;
using System.Threading;
using System.Windows.Forms;
using Triggernometry.Core;
using Triggernometry.UI.Forms;
using static Triggernometry.UI.Forms.GameConfigForm;

RealPlugin.Instance.RegisterNamedCallback(S7cEntry.Info.ConfigName, new Action<object, string>((_, __) => S7cEntry.Start()), null);

public static class S7cEntry
{
    public static ConfigInfo Info = new ConfigInfo("阿卡狄亚重量级 M9-12 宝宝椅", null, "阿洛", "${env:prefix}_cfg");
    static Color Blue = Color.FromArgb(0x22, 0x66, 0xff);
    public static void RunConfigForm()
    {
        try { _RunConfigForm(); }
        catch (Exception ex)
        {
            MessageBox.Show("配置界面运行时遇到问题：\n\n" + ex, Info.Name, MessageBoxButtons.OK, MessageBoxIcon.Error);
        }
    }

    private static void _RunConfigForm()
    {
        GameConfigForm form = new GameConfigForm(Info);
        form.Shown += (sender, e) => RealPlugin.Instance.InvokeNamedCallback("command", "/e <se.9>");

        BijectDictionary<string, string> cbxItems;
        Option optionCtrl;
        Label label;
        string hint = "";
        // 在下面倒序添加各个选项组（组内的选项正序）

        OptionsTableLayoutPanel tableM8s = form.AddOptionGroup(" 占位 ");
        /*
        cbxItems = new BijectDictionary<string, string>(
            ("国", " [国服] 斜八方固定位置"),
            ("美", " [美服] 浮游炮安全区方向 TH 顺 D 逆")
        );
        optionCtrl = new OptionCbx("　钢铁浮游炮时的分散站位", "M8s_钢铁站位", cbxItems, "国");
        form.AddOption(optionCtrl, tableM8s);
        cbxItems = new BijectDictionary<string, string>(
            ("1", " [国服] 先撞绿色风球"),
            ("0", " [美服] 先撞黄色土立方体")
        );
        optionCtrl = new OptionCbx("　小怪阶段消除风土 debuff 顺序", "M8s_b先撞绿", cbxItems, "1");
        form.AddOption(optionCtrl, tableM8s);
        */
       
        #region 使用须知

        OptionsTableLayoutPanel tableInfo = form.AddOptionGroup(" 使用须知 ");

        form.AddLabel("· 使用前必须查看使用说明，见绝伊甸触发器分组下的「使用说明」触发器。", tableInfo)
            .ForeColor = Blue;

        //label = form.AddLabel("· 下方部分选项上悬停鼠标可以显示详细说明。", tableInfo);
        //label.ForeColor = Blue;

        #endregion 使用须知

        Application.OpenForms.OfType<GameConfigForm>().ToList().ForEach(f => f.Close());
        form.Run();
    }


    [STAThread]
    public static void Start()
    {
        try
        {
            Application.OpenForms.OfType<GameConfigForm>().ToList().ForEach(f => f.Close());
            Thread staThread = new Thread(new ThreadStart(RunConfigForm));
            staThread.SetApartmentState(ApartmentState.STA);
            staThread.Start();
            staThread.Join();
        }
        catch (Exception ex)
        {
            MessageBox.Show("用户配置表单运行错误：\n" + ex, Info.Name, MessageBoxButtons.OK, MessageBoxIcon.Error);
        }
    }
}

