using Triggernometry;

public static class LogFilter
{
    private static readonly HashSet<string> junkEntities = new HashSet<string>
    {
        // 召唤
        "宝石兽", "Carbuncle", "カーバンクル",
        "红宝石兽", "Ruby Carbuncle", "カーバンクル・ルビー",
        "黄宝石兽", "Topaz Carbuncle", "カーバンクル・トパーズ",
        "绿宝石兽", "Emerald Carbuncle", "カーバンクル・エメラルド",
        "伊弗利特之灵", "Ifrit-Egi", "イフリート・エギ",   // 英语有没有 - ？
        "泰坦之灵", "Titan-Egi", "タイタン・エギ",
        "迦楼罗之灵", "Garuda-Egi", "ガルーダ・エギ",
        "红宝石伊弗利特", "Ruby Ifrit", "イフリート・ルビー",
        "黄宝石泰坦", "Topaz Titan", "タイタン・トパーズ",
        "绿宝石迦楼罗", "Emerald Garuda", "ガルーダ・エメラルド",
        "亚灵神巴哈姆特", "Demi-Bahamut", "デミ・バハムート",
        "亚灵神不死鸟", "Demi-Phoenix", "デミ・フェニックス",
        "光龙巴哈姆特？", "Solar Bahamut", "ソル・バハムート", 
        // 黑骑
        "英雄的掠影", "Esteem", "英雄の影身", 
        // 机工
        "车式浮空炮塔", "Rook Autoturret", "オートタレット・ルーク",
        "后式自走人偶", "Automaton Queen", "オートマトン・クイーン", 
        // 占星
        "地星", "Earthly Star", "アーサリースター", 
        // 学者
        "朝日小仙女", "Eos", "フェアリー・エオス",
        "夕月小仙女", "Selene", "フェアリー・セレネ",
        "炽天使", "Seraph", "セラフィム",
        // 白魔
        "礼仪之铃", "Liturgy of the Bell", "リタージー・オブ・ベル",
        // 忍者
        "分身", "Bunshin", "分身"
    };

    private static readonly List<Regex> junkPatterns = new List<Regex>
    {
        new Regex(@"^.{15}\S+ (1[4-9]|2[67]|10[578]|105:[^:]+|1[AE]:[^:]*:[^:]*:[^:]*):1.{7}:", RegexOptions.Compiled),
        new Regex(@"^.{15}\S+ 00:(?!0039|0044|0839)", RegexOptions.Compiled),
        new Regex(@"^.{15}\S+ (1[8F]|2[45]|F[BE]|10E|105:Change):", RegexOptions.Compiled),
        new Regex(@"^.{15}\S+ 26:.{50}.+$", RegexOptions.Compiled),
    };

    public static void FilterLogs()
    {
        string clipboardData = Clipboard.GetText();

        string junkEntitiesPattern = string.Join("|", junkEntities.Select(Regex.Escape));
        Regex idPattern = new Regex($@":(40.{{6}}):({junkEntitiesPattern}):");
        HashSet<string> idsToRemove = idPattern.Matches(clipboardData).Cast<Match>().Select(m => $":{m.Groups[1].Value}:").ToHashSet();

        string[] lines = clipboardData.Split(new[] { "\r\n", "\r", "\n" }, StringSplitOptions.RemoveEmptyEntries);
        var filteredLines = lines.Where(line =>
            !idsToRemove.Any(id => line.Contains(id)) &&
            !junkPatterns.Any(pattern => pattern.IsMatch(line))
        ).ToArray();
        var result = string.Join("\r\n", filteredLines);
        if (string.IsNullOrEmpty(result))
        {
            Clipboard.Clear();
        }
        else
        {
            Clipboard.SetText(result);
        }
    }

    [STAThread]
    public static void Main()
    {
        Thread thread = new Thread(() =>
        {
            FilterLogs();
        });
        thread.SetApartmentState(ApartmentState.STA);
        thread.Start();
        thread.Join();
        string result = CultureInfo.CurrentUICulture.TwoLetterISOLanguageName == "zh" ? "筛选完成" : "Filtered";
        Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(false, "tmp_tts", result);
    }
}

Triggernometry.RealPlugin.plug.RegisterNamedCallback("LogFilter", new System.Action<object, string>((_, __) => LogFilter.Main()), "MnFeN 阿洛");

using System;
using System.Text.RegularExpressions;
using System.Windows.Forms;
using System.Collections.Generic;
using System.Linq;
using System.Threading;
using System.Globalization;
using Triggernometry;

public static class LogAnonymizer
{
    private static Regex RegexId = new Regex(@"(?<=:)(?<id>10[0-9A-F]{6})(?=:|$)");
    private static Regex RegexName = new Regex(@"\G:(?<name>[A-Z][a-z'-]{1,14} [A-Z][a-z'-]{1,14}|[^a-z:\d][^A-Z:\d]{0,5})(?=:|$)");

    public static void Anonymize()
    {
        string clipboardData = Clipboard.GetText();
        var id2Name = new Dictionary<string, string>();

        // 从当前复制的日志中提取所有 id，并尝试对应到后面的名字
        foreach (Match match in RegexId.Matches(clipboardData))
        {
            if (!match.Success) continue; // 以防万一

            string id = match.Groups["id"].Value;
            int searchStart = match.Index + match.Length;
            // 正则 \G 用于确保仅从 searchStart 位置开始匹配紧随其后的名字
            Match nameMatch = RegexName.Match(clipboardData, searchStart);

            if (nameMatch.Success)
            {
                string name = nameMatch.Groups["name"].Value;
                if (!id2Name.TryGetValue(id, out string currentName) || currentName == "")
                {
                    id2Name[id] = name;
                }
                else
                {
                    if (currentName != name)
                    {
                        MessageBox.Show($"ID {id} name inconsistent: '{currentName}' vs '{name}'");
                    }
                }
            }
            else
            {
                if (!id2Name.ContainsKey(id))
                {
                    id2Name[id] = "";
                }
            }
        }

        // 读取当前小队列表，如果记录的所有 id 均在小队列表中，则用小队列表的顺序替换
        var party = Triggernometry.Interpreter.StaticHelpers.GetListVariable(false, "party")?.Values?.Select(v => v.ToString())?.ToList();
        Dictionary<string, int> id2Idx;
        if (party != null && id2Name.Keys.All(id => party.Contains(id)))
        {
            id2Idx = id2Name.Keys.ToDictionary(id => id, id => party.IndexOf(id) + 1);
        }
        else
        {
            id2Idx = id2Name.Keys
                .Select((id, idx) => (id, idx))
                .ToDictionary(pair => pair.id, pair => pair.idx + 1);
            
            // 试图修正自己的 idx
            do
            {
                // 获取自己在当前小队列表的序号
                var myId = Triggernometry.FFXIV.Entity.MyHexID;
                var myIdx = party?.IndexOf(Triggernometry.FFXIV.Entity.MyHexID) ?? -1;
                myIdx++;
                RealPlugin.plug.InvokeNamedCallback("command", $"/e myIdx = {myIdx}");
                if (myIdx <= 0) break;

                // 查找当前占用了自身 idx 的 id
                var otherPair = id2Idx.FirstOrDefault(pair => pair.Value == myIdx);
                if (!otherPair.Equals(default(KeyValuePair<string, int>))) // 有人占用了自身 idx
                {
                    if (id2Name.ContainsKey(myId)) // 日志包含自己：二人对换
                    {
                        id2Idx[otherPair.Key] = id2Idx[myId];
                        id2Idx[myId] = myIdx;
                    }
                    else if (id2Idx.Count <= party.Count) // 日志没包含自己，且日志不满小队人数
                    {
                        id2Idx[otherPair.Key] = id2Idx.Count + 1;
                    }
                }
                else // 没有人占用了自身 idx
                {
                    if (id2Name.ContainsKey(myId)) // 日志包含自己
                    {
                        id2Idx[myId] = myIdx;
                    }
                }
            } while (false);
        }
/*
        // 替换自身 id 与名字
        if (Triggernometry.FFXIV.Entity.MyID != 0)
        {
            var id = Triggernometry.FFXIV.Entity.MyHexID;
            var name = Triggernometry.FFXIV.Entity.MyName;
            clipboardData = clipboardData.Replace($"{id}:{name}", "$" + "{_me.id}:自身");
            clipboardData = clipboardData.Replace(id, "$" + "{_me.id}");
            if (clipboardData.Contains(name)) // 自己的 id 变动了
            { 
                var regexMe = new Regex(@"(?<id>10[0-9A-F]{6}):" + Regex.Escape(name) + @"(?=:|$)");
                var myIds = regexMe.Matches(clipboardData).Cast<Match>().Select(m => m.Groups["id"].Value).ToList();
                var hashset = new HashSet<string>(myIds);
                if (hashset.Count > 1)
                { 
                    MessageBox.Show($"发现自身名字 \"{name}\" 对应多个 ID：\n{string.Join("\n", hashset)}");
                }
                id = hashset.FirstOrDefault();
                if (id != null)
                {
                    clipboardData = clipboardData.Replace(id, "$" + "{_me.id}"); 
                }
                clipboardData = clipboardData.Replace(name, "自身");
            }
        }
*/
        // 替换小队 id 与名字
        foreach (var pair in id2Idx)
        {
            string id = pair.Key;
            int idx = pair.Value;
            if (id2Name.TryGetValue(id, out string name) && name != "")
            {
                clipboardData = clipboardData.Replace($"{id}:{name}", "$" + $"{{l:party[{idx}]}}:队员");
            }
            clipboardData = clipboardData.Replace(id, "$" + $"{{l:party[{idx}]}}");
        }

        // 输出
        if (string.IsNullOrEmpty(clipboardData))
        {
            Clipboard.Clear();
        }
        else
        {
            Clipboard.SetText(clipboardData);
        }
    }

    [STAThread]
    public static void Main()
    {
        Thread thread = new Thread(() =>
        {
            Anonymize();
        });
        thread.SetApartmentState(ApartmentState.STA);
        thread.Start();
        thread.Join();
    }
}

Triggernometry.RealPlugin.plug.RegisterNamedCallback(nameof(LogAnonymizer), new System.Action<object, string>((_, __) => LogAnonymizer.Main()), "MnFeN 阿洛");

