using Triggernometry.FFXIV;
using Triggernometry.PluginBridges.BridgeNamazu;
using Triggernometry.Utilities;
using Triggernometry.Utilities.Math;

public struct Info
{
    public const string Name = "AdvWm";
    public const string NameCN = "高级标点工具箱";
    public const string Version = "${env:version}";
    public const string Author = "阿洛 MnFeN";

    public static string InitInfo => $"{Name}：{NameCN} v{Version} by {Author} 已成功初始化。";
}

RealPlugin.plug.RegisterNamedCallback("AdvWm", new Action<object, string>(AdvWm.NamedCallback), null, registrant: $"{Info.NameCN} v{Info.Version}");
RealPlugin.plug.InvokeNamedCallback("NamazuLog", Info.InitInfo);
Interpreter.StaticHelpers.Log(RealPlugin.DebugLevelEnum.Custom, Info.InitInfo);

public class AdvWm
{
    string _command;
    MultiLineRawArgs _data;
    public AdvWm(string command) => _command = command;

    public static void Log(string message) => RealPlugin.plug.InvokeNamedCallback("command", $"/e {message}");

    public static void NamedCallback(object _, string input)
    {
        var commands = input.Split(new string[] { "---" }, StringSplitOptions.None);
        foreach (var command in commands)
        {
            new AdvWm(command).Execute();
        }
    }

    static readonly HashSet<string> postNamazuWaymarkKeywords = new HashSet<string> {
        "save", "backup", "load", "restore", "reset", "clear", "public"
    };

    private void Execute()
    {
        // 首先判断是否为关键词模式
        string simpleCmd = _command.ToLower().Trim();
        if (postNamazuWaymarkKeywords.Contains(simpleCmd))
        {
            RealPlugin.plug.InvokeNamedCallback("place", simpleCmd);
            return;
        }

        // 初始化 解析键值对
        _data = new MultiLineRawArgs(_command);
        // 提取共通参数
        string action = _data.TryGet("Action", out action) ? action : "";
        bool log = !_data.TryGet("Log", out string rawLog) || bool.Parse(rawLog.ToLower());
        bool local = !_data.TryGet(out string rawLocal, "Local", "LocalOnly") || bool.Parse(rawLocal.ToLower());

        Waymarks waymarks;
        switch (action?.ToLower())
        {
            case "getversion":
                Interpreter.StaticHelpers.SetScalarVariable(isPersistent: false, $"{Info.Name}_version", Info.Version); return;
            case "encode":
                {
                    bool isAnonymous = _data.Get("Anonymous").FromDataString<bool>();
                    WaymarksEncoder.EncryptedMessageHandler.EncodeMessageAndSendWaymarks(_data.Get("data"), isAnonymous);
                    return;
                }
            case "decode":
                WaymarksEncoder.EncryptedMessageHandler.DecodeMessageAndPrint();
                return;
            case "ask":
                {
                    bool isAnonymous = _data.Get("Anonymous").FromDataString<bool>();
                    bool isAlliance = _data.Get("IsAlliance").FromDataString<bool>();
                    WaymarksEncoder.UserQuery.Ask(isAnonymous, isAlliance);
                    return;
                }
            case "answer":
                {
                    bool isAlliance = _data.Get("IsAlliance").FromDataString<bool>();
                    WaymarksEncoder.UserQuery.Answer(isAlliance); 
                    return;
                }
            case "getusers":
                WaymarksEncoder.UserQuery.GetUsersResult(); return;
            case "polar": // 不推荐使用，仅为兼容旧版本，可被 default 模式完全替代
                waymarks = ParseWaymarksPolar(); break;
            case "circle":
                waymarks = ParseWaymarksCircle(); break;
            case "arc":
                waymarks = ParseWaymarksArc(); break;
            case "linear":
            case "connect":
            case "linearconnect":
                waymarks = ParseWaymarksLinearConnect(); break;
            // case "absolute":  case "relative":  case "place": 旧版本的指令全部整合为默认的标点模式
            default:
                waymarks = ParseWaymarksDefault(); break;
        }
        // Log($"初始：\n{waymarks}");
        TryApplyScales(waymarks);
        // Log($"伸缩：\n{waymarks}");
        TryApplyRotation(waymarks);
        // Log($"旋转：\n{waymarks}");
        TryApplyCenter(waymarks);
        // Log($"平移：\n{waymarks}");
        waymarks.LocalOnly = local;
        waymarks.Log = log;
        waymarks.Mark();
    }

    /// <summary> 根据提供的所有坐标，生成一组初始标点。 </summary>
    private Waymarks ParseWaymarksDefault()
    {
        Waymarks waymarks = new Waymarks();
        foreach (string name in Waymark.WaymarkNames)  // a b c d 1 2 3 4
        {
            if (_data.TryGet(name, out string rawCoord))
            {
                waymarks.Add(Waymark.Parse(name, rawCoord));
            }
        }
        return waymarks;
    }

    /// <summary> 根据提供的所有极坐标，生成一组初始标点。建议使用 Default 替代。 </summary>
    private Waymarks ParseWaymarksPolar()
    {
        Waymarks waymarks = new Waymarks();
        foreach (string name in Waymark.WaymarkNames)  // a b c d 1 2 3 4
        {
            if (_data.TryGet(name, out string rawPolarCoord))
            {   // 已重构为接近正常方法的处理逻辑
                XIVCoord polarCoord = XIVCoord.ParseRawData(rawPolarCoord.StartsWith("polar ") ? rawPolarCoord : $"polar {rawPolarCoord}");
                waymarks.Add(new Waymark(name, polarCoord.ToCartesian()));
            }
        }
        return waymarks;
    }

    /// <summary> 根据提供的半径和标点名，生成一组排列成圆周的初始标点。 </summary>
    private Waymarks ParseWaymarksCircle()
    {
        Waymarks waymarks = new Waymarks();
        double rCardinal = MathParser.Parse(_data.Get("R"));
        double rIntercard = _data.TryGet("R2", out string rawR2) ? MathParser.Parse(rawR2) : rCardinal;
        string[] usedWaymarkNames = _data.Get("Waymarks").Select(c => char.ToLower(c).ToString()).ToArray(); // 用哪些点 相对正北逆时针 如 A4D3C2B1
        double step = 2 * PI / usedWaymarkNames.Length; // 相邻标点的角度差
        int count = 0;
        foreach (string name in usedWaymarkNames)  // a b c d 1 2 3 4
        {
            if (Waymark.WaymarkNames.Contains(name))
            {
                var r = (count & 1) == 0 ? rCardinal : rIntercard;
                var θ = count * step - PI;
                waymarks.Add(new Waymark(name, new PolarCoord(r, θ, 0)));
            }
            count++;
        }
        return waymarks;
    }

    /// <summary> 根据提供的半径、标点名、圆心角，生成一组排列成弧线的初始标点。 </summary>
    private Waymarks ParseWaymarksArc()
    {
        Waymarks waymarks = new Waymarks();
        double r = MathParser.Parse(_data.Get("R"));   // 半径
        string[] usedWaymarkNames = _data.Get("Waymarks").Select(c => char.ToLower(c).ToString()).ToArray(); // 用哪些点 相对正北逆时针 如 A4D3C2B1
        int stepCount = usedWaymarkNames.Length - 1;

        // 圆弧的圆心角，不提供的时候默认为使标点紧邻的角度（弧长约 2.6）
        double dθ = _data.TryGet(out string rawdθ, "dθ", "dTheta") ? MathParser.Parse(rawdθ) : (stepCount >= 1) ? 2.6 / r * stepCount : 0;
        double θStep = (stepCount >= 1) ? dθ / stepCount : 0; // 相邻标点的角度差
        int count = 0;
        foreach (string name in usedWaymarkNames)  // a b c d 1 2 3 4
        {
            if (Waymark.WaymarkNames.Contains(name)) // 允许使用非标点名的字符占位等分点，不会生成标点
            {
                PolarCoord polarCoord = new PolarCoord(r, (count - stepCount / 2.0) * θStep - PI, 0);
                waymarks.Add(new Waymark(name, polarCoord));
            }
            count++;
        }
        return waymarks;
    }

    /// <summary> 根据提供的起点、终点、标点名，生成线性插值的初始标点。 </summary>
    private Waymarks ParseWaymarksLinearConnect()
    {
        Waymarks waymarks = new Waymarks();
        string[] usedWaymarkNames = _data.Get("Waymarks").Select(c => char.ToLower(c).ToString()).ToArray(); // 用哪些点 起点到终点 如 A4D3C2B1
        if (usedWaymarkNames.Length < 2)
        {
            throw new Exception("AdvWm: LinearConnect 模式下，提供的标点数量不足 2");
        }
        XIVCoord startCoord = XIVCoord.ParseRawData(_data.Get("start"));
        XIVCoord endCoord = XIVCoord.ParseRawData(_data.Get("end"));
        XIVCoord vector = endCoord - startCoord;
        double totalDistance = vector.Length;
        // Log($"{vector}");
        int count = 0;
        foreach (string name in usedWaymarkNames)  // A B C D 1 2 3 4
        {
            if (Waymark.WaymarkNames.Contains(name))
            {
                double percentage = (double)count / (usedWaymarkNames.Length - 1);  // 标点默认等分
                if (_data.TryGet(name, out string rawDistance))   // 也可以不等分，自定义标点位置
                {
                    if (rawDistance.EndsWith(" m"))      // 以 A: 5 m 形式提供的距离
                    {
                        // @d 代表总长度，如 A: @d - 5 m 意为终点前 5 m
                        string strTotalDistance = totalDistance.ToString(MathParser.CultureInfo);
                        rawDistance = rawDistance.Replace("@d", strTotalDistance);
                        double distance = MathParser.Parse(rawDistance.Substring(0, rawDistance.Length - 2));
                        percentage = distance / totalDistance;
                    }
                    else                                // 以 A: 0.125 形式提供的百分比
                    {
                        percentage = MathParser.Parse(rawDistance);
                    }
                }

                XIVCoord coord = startCoord + percentage * vector;
                waymarks.Add(new Waymark(name, coord));
            }
            count++;
        }
        return waymarks;
    }

    /// <summary> 如果当前指令提供了任何伸缩，将伸缩变换应用到给定的一组标点坐标。 </summary>
    /// <returns></returns>
    private void TryApplyScales(Waymarks waymarks)
    {
        // 首先尝试解析 Scale 和具体的 ScaleX, ScaleY, ScaleZ 参数
        bool hasScale = _data.TryGet("Scale", out string rawScale);
        bool hasScaleX = _data.TryGet("ScaleX", out string rawScaleX);
        bool hasScaleY = _data.TryGet("ScaleY", out string rawScaleY);
        bool hasScaleZ = _data.TryGet("ScaleZ", out string rawScaleZ);

        // 未给定参数则免去解析直接返回
        if (!hasScale && !hasScaleX && !hasScaleY && !hasScaleZ) return;

        // 如果存在 scale 参数，按照 scaleX ?? scale ?? 1 的优先级解析
        double defaultScale = hasScale ? MathParser.Parse(rawScale) : 1;

        double scaleX = hasScaleX ? MathParser.Parse(rawScaleX) : defaultScale;
        double scaleY = hasScaleY ? MathParser.Parse(rawScaleY) : defaultScale;
        double scaleZ = hasScaleZ ? MathParser.Parse(rawScaleZ) : defaultScale;

        // 如果任一缩放倍率不是 1，则缩放
        if (Abs(scaleX - 1) > 1e-5 || Abs(scaleY - 1) > 1e-5 || Abs(scaleZ - 1) > 1e-5)
        {
            foreach (Waymark wm in waymarks)
            {
                wm.Coord = wm.Coord.ScaleBy(scaleX, scaleY, scaleZ);
            }
        }
    }

    /// <summary> 如果当前指令提供了旋转，将伸缩变换应用到给定的一组标点坐标。 </summary>
    /// <returns></returns>
    private void TryApplyRotation(Waymarks waymarks)
    {
        if (_data.TryGet(out string rawθ, "θ", "Theta"))
        {
            double θ = MathParser.Parse(rawθ);
            foreach (Waymark wm in waymarks)
            {
                wm.Coord = wm.Coord.RotateTo(θ);
            }
        }
    }

    /// <summary> 如果当前指令提供了场地中心，将场地中心应用到给定的一组标点坐标。 </summary>
    void TryApplyCenter(Waymarks waymarks)
    {
        if (_data.TryGet(out string rawCenter, "O", "Center"))
        {
            XIVCoord centerCoord = XIVCoord.ParseRawData(rawCenter);
            foreach (Waymark wm in waymarks)
            {
                wm.Coord = wm.Coord.MoveTo(centerCoord);
            }
        }
    }

}

#region Waymark(s)

public enum WaymarkType { A, B, C, D, One, Two, Three, Four }
public class Waymark
{
    public WaymarkType Type { get; set; }
    public XIVCoord Coord { get; set; }
    public bool Ignore { get; set; }
    public bool Active { get; set; }

    /// <summary>
    /// 用于遍历时保证输出顺序
    /// </summary>
    public static readonly string[] WaymarkNames = new string[] { "a", "b", "c", "d", "1", "2", "3", "4" };
    public static readonly WaymarkType[] WaymarkTypes = new WaymarkType[]
    {
        WaymarkType.A, WaymarkType.B, WaymarkType.C, WaymarkType.D,
        WaymarkType.One, WaymarkType.Two, WaymarkType.Three, WaymarkType.Four
    };

    /// <summary>
    /// 从用户输入的标点类型转化为实际 Type
    /// </summary>
    public static readonly Dictionary<string, WaymarkType> TypeMap = Enumerable.Range(0, 8)
        .ToDictionary(i => WaymarkNames[i], i => WaymarkTypes[i]);

    public Waymark(WaymarkType type, XIVCoord coord = null, bool ignore = false)
    {
        Type = type;
        Coord = coord ?? new CartesianCoord(0, 0, 0);
        Active = coord != null;
        Ignore = ignore;
    }

    public Waymark(string rawType, XIVCoord coord = null, bool ignore = false)
    {
        if (TypeMap.TryGetValue(rawType, out var mappedType))
        {
            Type = mappedType;
        }
        else
        {
            throw new Exception($"AdvWm: {rawType} 不是合法的标点名之一（A B C D 1 2 3 4）。");
        }
        Coord = coord ?? new CartesianCoord(0, 0, 0);
        Active = coord != null;
        Ignore = ignore;
    }

    public static Waymark Parse(string rawType, string rawCoord, bool ignore = false)
    {
        Waymark wm = new Waymark(rawType, null, ignore);

        switch (rawCoord.Trim().ToLower())
        {
            // 清除标点
            case "clear":
                wm.Active = false;
                wm.Coord = new CartesianCoord(0, 0, 0);
                break;
            // 变相地“清除”标点  不会有淡入淡出动画
            case "fakeclear":
                wm.Active = true;
                wm.Coord = new CartesianCoord(0, 0, 1000);
                break;
            default:
                wm.Active = true;
                wm.Coord = XIVCoord.ParseRawData(rawCoord);
                break;
        }

        return wm;
    }

    public string Jsonify()
    {
        if (Ignore)
            return "";
        if (Active)
            return $"\"{Type}\": {{ {Coord.Jsonify()}, \"Active\": true }}";
        else
            return $"\"{Type}\": {{}}";
    }

    public override string ToString() => Jsonify();
}

public sealed class Waymarks : IEnumerable<Waymark>
{
    private readonly Dictionary<WaymarkType, Waymark> _waymarks;
    public bool Log = true;
    public bool LocalOnly = true;

    public Waymarks()
    {
        _waymarks = new Dictionary<WaymarkType, Waymark>();
        foreach (var type in Waymark.WaymarkTypes)
        {
            _waymarks[type] = new Waymark(type, ignore: true);
        }
    }

    public Waymark this[WaymarkType type]
    {
        get => _waymarks[type];
        set => _waymarks[type] = value;
    }

    IEnumerator IEnumerable.GetEnumerator() => GetEnumerator();
    public IEnumerator<Waymark> GetEnumerator()
    {
        foreach (var type in Waymark.WaymarkTypes)
        {
            if (_waymarks.TryGetValue(type, out Waymark waymark))
            {
                yield return waymark;
            }
        }
    }

    public void Add(Waymark wm)
    {
        if (wm == null || wm.Ignore) return;
        else _waymarks[wm.Type] = wm;
    }

    public string Jsonify()
    {
        var jsonList = _waymarks.Values.Where(wm => wm?.Ignore == false)
            .Select(wm => "    " + wm.Jsonify()).ToList();
        if (!Log)
            jsonList.Add($"    \"Log\": false");
        if (!LocalOnly)
            jsonList.Add($"    \"LocalOnly\": false");
        string data = string.Join(",\n", jsonList);
        return $"{{\n{string.Join(",\n", jsonList)}\n}}";
    }
    public override string ToString() => Jsonify();

    public void Mark()
    {
        RealPlugin.plug.InvokeNamedCallback("place", this.Jsonify());
        //RealPlugin.plug.InvokeNamedCallback("command", $"/e {this}");
    }
}

#endregion Waymark(s)

public static class WaymarksEncoder
{
    /// <summary> 用于表示用户状态的 flags。 </summary>
    [Flags]
    public enum StatusFlags : byte
    {
        None = 0,
        /// <summary> 是否将自身标记为匿名。 </summary>
        IsAnonymous = 1,
        /// <summary> 是否将编码的 ID 替换为空值。 </summary>
        IsIdHidden = 2,
        /// <summary> 鲶鱼精邮差的 WayMark 功能是否启用。 </summary>
        IsWaymarkEnabled = 4,
        /// <summary> 是否主动请求数据 / 主动发送消息。 </summary>
        IsInitiator = 8,
        /// <summary> 是否是团队模式（编码实际 id 而非小队次序） </summary>
        IsAllianceMode = 16,
    }

    /// <summary> 将 StatusFlags 编码为 z 坐标的小数部分。</summary>
    public static float FlagsToOffsetZ(StatusFlags flags)
    {
        return (byte)flags * 0.01f + 0.01f;
    }

    /// <summary> 将用于编码 StatusFlags 的 z 坐标转化回 StatusFlags。</summary>
    public static StatusFlags ZToFlags(float z)
    {
        return (StatusFlags)(byte)Round((z - Floor(z) - 0.01) * 100);
    }

    /// <summary> 从用户配置中提取 IsAnonymous 与 IsIdHidden 信息 </summary>
    static StatusFlags UserConfigAnonymousFlags
    {
        get
        {
            var data = Interpreter.StaticHelpers.GetScalarVariable(true, "AdvWm_Anonymous");
            StatusFlags result;
            if (data == null)
            {
                result = StatusFlags.None;
            }
            else if (!byte.TryParse(data, out var rawResult))
            {
                result = StatusFlags.IsAnonymous;
            }
            else
            {
                result = (StatusFlags)rawResult;
                result &= (StatusFlags.IsAnonymous | StatusFlags.IsIdHidden);
            }
            // 保存正确格式，或删除
            Interpreter.StaticHelpers.SetScalarVariable(true, "AdvWm_Anonymous", result != StatusFlags.None ? (byte?)result : null);
            return result;
        }
    }

    /// <summary> 用场地标点的坐标加密、解密文本信息，仅通过标点实现用户之间的加密通信频道。</summary>
    public static class EncryptedMessageHandler
    {
        /// <summary> 将一个 ±16384 以内、三位小数精度的 float 游戏坐标编码为 uint24。</summary>
        static int CoordToInt(float coord)
        {
            // 标点数据只有三位精度。
            // float 在 ±16384 内时可保证第三位小数准确，小于这个阈值的最大 2^n / 1000 为 ±8388.608，恰好 24 bit
            int encodedInt = (int)Round((double)coord * 1000);
            if (encodedInt < 0)            // -8388608 ~ -1
                encodedInt += 0xFFFFFF;    // 转换为 8388608 (0x800000) ~ 16777215 (0xFFFFFF)
            return encodedInt;
        }

        /// <summary> 从 uint24 编码的整数解码为 ±16384 以内、三位小数精度的 float 游戏坐标（这里用 double 是为了精度检验）。</summary>
        static double IntToCoord(int encodedInt) => (encodedInt > 0x7FFFFF ? encodedInt - 0xFFFFFF : encodedInt) / 1000.0;

        /// <summary> ID (10XXXXXX) 实际数据量相当于 uint24，将坐标转换为发送者 ID </summary>
        static uint? CoordToId(float coord) => coord == 0 ? null : (uint?)(CoordToInt(coord) + 0x10000000);

        /// <summary> 用于表明发送消息的特征 Y/Z 基准值。</summary>
        const float A_YZ = 531;

        /// <summary> 
        /// 编码一串文本，以标点形式发送：A 点用于编码发送者 ID 和状态标志，B-4 用于编码文本内容。<br />
        /// · AX => uint24 ID ?? 0; <br />
        /// · AY => 特征值 531; <br />
        /// · AZ => 特征值 531 + flagOffset <br /><br />
        /// </summary>
        public static void EncodeMessageAndSendWaymarks(string msg, bool wantAnonymous)
        {
            try
            {
                // 将信息编码为一组 uint24
                byte[] byteArray = Encoding.UTF8.GetBytes(msg);
                if (byteArray.Length > 63)
                    throw new Exception("输入字符串过长，不能超过 63 字节。");
                Array.Resize(ref byteArray, 63);
                List<int> integers = new List<int>();
                for (int i = 0; i < 21; i++)
                {
                    // 每三个字节合并为一个 uint24 整数（大端序）
                    var current = (byteArray[i * 3] << 16) | (byteArray[i * 3 + 1] << 8) | (byteArray[i * 3 + 2]);
                    var currentDouble = IntToCoord(current);
                    // 实际发现 ±8192 以外的小数在传给 SE 时有概率丢失小数精度，手动修复此问题
                    if (Abs(currentDouble) > 8192)
                    {
                        bool corrupted = false;
                        var currentErr = (double)(float)currentDouble - currentDouble;
                        var prevDouble = currentDouble - (currentDouble >= 0 ? 0.001 : -0.001);
                        var prevErr = (double)(float)prevDouble - prevDouble;
                        if (currentDouble > 8192 && currentErr <= -0.00025) // 实测的阈值 0.00025，与 float 强转 int 的取整行为有关
                        {
                            if (prevErr <= -0.00025) // 可以校正
                            {
                                //Debug.Log(current, currentDouble, currentErr, "current++", prevDouble, prevErr);
                                current++;
                            }
                            else corrupted = true; // 编码后的值被两个原始值共享，无法校正
                        }
                        else if (currentDouble < -8192 && currentErr >= 0.00025)
                        {
                            if (prevErr >= 0.00025)
                            {
                                //Debug.Log(current, currentDouble, currentErr, "current--", prevDouble, prevErr);
                                current--;
                            }
                            else corrupted = true;
                        }
                        if (corrupted) 
                            throw new Exception("<se.11>\n你恰好输入了编码会造成精度丢失的字符组合。\n你可以尝试调整文本，如在最前面加一个英文句号\".\"。");
                    }
                    integers.Add(current);
                }

                // 读取配置，应用状态
                var flags = StatusFlags.None;
                if (wantAnonymous)  // 要求匿名时设置匿名
                {
                    flags |= StatusFlags.IsAnonymous;
                    if (UserConfigAnonymousFlags.HasFlag(StatusFlags.IsIdHidden))
                        flags |= StatusFlags.IsIdHidden;
                }
                flags |= StatusFlags.IsInitiator;
                if (BridgeNamazu.NamazuPlugin.ActionEnabled["WayMark"])
                {
                    flags |= StatusFlags.IsWaymarkEnabled;
                }

                string advWm = $"Local: false";
                float ax = flags.HasFlag(StatusFlags.IsIdHidden) ? 0 : (float)IntToCoord((int)(Triggernometry.FFXIV.Entity.MyID - 0x10000000));
                float offsetZ = FlagsToOffsetZ(flags);
                advWm += $"\nA: {ax}, {A_YZ}, {A_YZ + offsetZ}";
                string[] names = new string[] { "B", "C", "D", "1", "2", "3", "4" };
                for (int i = 0; i < 7; i++)
                {
                    if (integers[i * 3] == 0 && integers[i * 3 + 1] == 0 && integers[i * 3 + 2] == 0)
                        advWm += $"\n{names[i]}: clear";
                    else
                    {
                        float x = (float)IntToCoord(integers[i * 3]);
                        float y = (float)IntToCoord(integers[i * 3 + 1]);
                        float z = (float)IntToCoord(integers[i * 3 + 2]);
                        advWm += $"\n{names[i]}: {x}, {y}, {z}";
                    }
                }
                BridgeNamazu.NamazuPlugin.ExecuteWithForcedModuleState("WayMark", () => RealPlugin.plug.InvokeNamedCallback("AdvWm", advWm));
            }
            catch (Exception e)
            {
                AdvWm.Log($"发送失败：{e.Message}");
                //Debug.Show(e);
            }
        }

        /// <summary> 解码当前标点为文本，并检测发送者 </summary>
        public static void DecodeMessageAndPrint()
        {
            List<byte> byteList = new List<byte>();
            var waymarks = Triggernometry.Utilities.Memory.Waymarks.Read();
            uint? senderId = CoordToId(waymarks.A.X);
            StatusFlags flags = ZToFlags(waymarks.A.Z);
            var sender = new UserEntry(senderId ?? 0, flags);

            foreach (var waymark in waymarks.Skip(1)) // 跳过用于表明密语消息的 A 点
            {
                if (!waymark.Active) break;
                foreach (var coord in new[] { waymark.X, waymark.Y, waymark.Z })
                {
                    int encodedInt = CoordToInt(coord);
                    // 从而将 ±8388.608 以内的 float 转换为 24 bit 数据，再分解为 3 字节
                    byteList.Add((byte)((encodedInt >> 16) & 0xFF));   // 高8位
                    byteList.Add((byte)((encodedInt >> 8) & 0xFF));    // 中8位
                    byteList.Add((byte)(encodedInt & 0xFF));           // 低8位
                }
            }
            // 去除末尾多余的 0 字节
            int firstZero = byteList.FindIndex(b => b == 0);
            if (firstZero >= 0)
            {
                byteList = byteList.GetRange(0, firstZero);
            }
            var msg =  Encoding.UTF8.GetString(byteList.ToArray());
            var se = msg.Contains("<se.") ? "" : " <se.10>";
            var fullMsg = $@"[AdvWm] 加密通讯频道
如果你对此消息有疑问，可输入 /e ?tell
—————————————————
{sender} 发送了消息：{se}
{msg}";
            AdvWm.Log(fullMsg);
        }
    }

    public static class UserQuery
    {
        /// <summary> 将 0x10000000 - 0x10FFFFFF 的 id 编码为 80.00 - 120.95 范围内的两个 float 坐标 </summary>
        static (float, float) AllianceModeIdToCoords(uint id)
        {
            id -= 0x10000000;
            return (80 + ((id >> 12) & 0xFFF) / 100f, 80 + (id & 0xFFF) / 100f);
        }

        public static void Ask(bool wantAnonymous, bool isAlliance)
        {
            var flags = StatusFlags.None;
            if (wantAnonymous)  // 要求匿名时设置匿名
            { 
                flags |= StatusFlags.IsAnonymous;
                if (UserConfigAnonymousFlags.HasFlag(StatusFlags.IsIdHidden))
                    flags |= StatusFlags.IsIdHidden;
            }
            flags |= StatusFlags.IsInitiator;
            if (BridgeNamazu.NamazuPlugin.ActionEnabled["WayMark"])
            {
                flags |= StatusFlags.IsWaymarkEnabled;
            }
            if (isAlliance)
            {
                flags |= StatusFlags.IsAllianceMode;
            }
            AskOrAnswer(flags);
        }

        public static void Answer(bool isAlliance)
        {
            var flags = UserConfigAnonymousFlags;
            if (BridgeNamazu.NamazuPlugin.ActionEnabled["WayMark"])
            {
                flags |= StatusFlags.IsWaymarkEnabled;
            }
            if (isAlliance)
            {
                flags |= StatusFlags.IsAllianceMode;
            }
            AskOrAnswer(flags);
        }

        static List<string> WaymarkNames = new List<string> { "A", "B", "C", "D", "1", "2", "3", "4" };

        /// <summary> 
        /// 通过改变标点，发起关于谁在使用此工具箱的询问或应答。<br />
        /// 小队模式查询：仅修改自己 ID 在小队中数值升序排序对应的标点 <br />
        /// · X => (原始值 ?? 0) 近似至最接近的 ±*.*1，以末位 1 为标志位 <br />
        /// · Y => (原始值 ?? 0) 近似至最接近的 ±*.*1，以末位 1 为标志位 <br />
        /// · Z => (原始值 ?? 0) 取整 + flagOffset <br /><br />
        /// 团队模式查询：用 A 点标记，且清除其他所有标点 <br />
        /// · (AX, AY) => uint24 ID ?? (0, 0) <br />
        /// · AZ => 特征值 329 + flagOffset 
        /// </summary>
        private static void AskOrAnswer(StatusFlags flags)
        {
            try
            {
                int myIdx = 0;
                string myWaymark = "A";
                float x, y, z;

                // Party 模式：通过自身 ID 在小队中的排序指定使用的标点，XYZ 坐标大致保持不变，XY 近似到第二位小数为 1 作为识别符，Z 小数部分为 flags
                if (!flags.HasFlag(StatusFlags.IsAllianceMode))
                {
                    var myHexID = Triggernometry.FFXIV.Entity.GetMyself().HexID;
                    var ids = Triggernometry.FFXIV.Entity.GetEntities()
                        .Where(e => e.HexID == myHexID || (e.Type == EntityType.Pc && e.InParty)) // 只有自己的时候没有形成小队，加一个条件用于调试
                        .OrderBy(e => e.ID)
                        .Select(e => e.HexID)
                        .ToList();
                    // 如果隐藏 ID，则使用 A 点作为标记 
                    myIdx = !flags.HasFlag(StatusFlags.IsIdHidden) ? ids.IndexOf(myHexID) : 0;
                    if (myIdx < 0 || myIdx > 8)
                        throw new Exception($"小队数据错误：myID = {myHexID}, idx = {myIdx}");
                    myWaymark = WaymarkNames[myIdx];

                    // 获取当前的该标点信息 ?? (0, 0, 0)，以防标点闪烁
                    var currentWaymark = Memory.Waymarks.Read().ElementAt(myIdx);
                    if (currentWaymark.Active)
                    {
                        x = (float)Round(currentWaymark.X, 1);
                        y = (float)Round(currentWaymark.Y, 1);
                        z = (float)Floor(currentWaymark.Z + 0.01);
                    }
                    else (x, y, z) = (0f, 0f, 0f);
                    // 将 XY 添加末尾的 0.01 特征
                    x += (x >= 0 ? 0.01f : -0.01f);
                    y += (y >= 0 ? 0.01f : -0.01f);
                }
                // Alliance 模式：固定使用 A 点，用 XY 编码自身的 ID，Z 整数部分为特征数值 329，小数部分为 flags
                else
                {
                    if (!flags.HasFlag(StatusFlags.IsIdHidden))
                        (x, y) = AllianceModeIdToCoords(Triggernometry.FFXIV.Entity.GetMyself().ID);
                    else
                        (x, y) = (0f, 0f);
                    z = 329f;
                }

                // flags 编码到 z 的小数部分
                z += FlagsToOffsetZ(flags);

                // 生成高级标点工具箱的指令
                var waymarkCmds = WaymarkNames.Select(wm =>
                {
                    if (wm == myWaymark)
                        return $"{wm}: {x}, {y}, {z}";
                    else
                        return flags.HasFlag(StatusFlags.IsAllianceMode) ? $"{wm}: clear" : "";
                });
                string advWm = $"Local: false\n{string.Join("\n", waymarkCmds)}";
                BridgeNamazu.NamazuPlugin.ExecuteWithForcedModuleState("WayMark", () => RealPlugin.plug.InvokeNamedCallback("AdvWm", advWm));
            }
            catch (Exception e)
            {
                AdvWm.Log($"发送失败：{e.Message}");
            }
        }

        public static void GetUsersResult()
        {
            var users = Interpreter.StaticHelpers.GetListVariable(false, "AdvWm_users")?.Values?.Select(v => new UserEntry(v.ToString()))?.ToList()
                ?? throw new Exception("AdvWm_users 列表未存入任何信息。");
            var ask = users.FirstOrDefault(u => u.Flags.HasFlag(StatusFlags.IsInitiator))                 ?? throw new Exception("AdvWm_users 列表未存入任何发送者信息。");
            // 查询者非自己、查询者要求匿名、且自己非匿名模式时，不显示结果
            if (ask.Id != Triggernometry.FFXIV.Entity.MyID && 
                ask.Flags.HasFlag(StatusFlags.IsAnonymous) && 
                !UserConfigAnonymousFlags.HasFlag(StatusFlags.IsAnonymous)) 
                return;
            string msg = $@"[AdvWm] 看看誰是挂友！<se.10>
{(ask.Flags.HasFlag(StatusFlags.IsAllianceMode) ? "团队" : "小队" )}模式
发起询问者：
  · {ask}
所有使用者:
  · {string.Join("\n  · ", users?.Where(u => u != ask))} ";
            AdvWm.Log(msg);
        }

    }

    internal class UserEntry
    {
        public uint Id;
        public StatusFlags Flags;

        public UserEntry(uint id, StatusFlags flags)
        {
            Id = id;
            Flags = flags;
        }

        /// <summary> 接收如 10ABCDEF, 13 的输入</summary>
        public UserEntry(string data)
        {
            var d = data.Split(',');
            Id = uint.Parse(d[0], System.Globalization.NumberStyles.HexNumber);
            Flags = (StatusFlags)byte.Parse(d[1], System.Globalization.CultureInfo.InvariantCulture);
        }

        string GetEntityDesc()
        {
            bool isCN = !Triggernometry.FFXIV.Entity.GetMyself().Name.Contains(" ");
            var entity = Triggernometry.FFXIV.Entity.GetEntityByID(Id);
            return $"[{(isCN ? entity.Job.NameCN2 : entity.Job.NameTCN2)}] {entity.Name}";
        }

        public override string ToString()
        {
            string desc;

            // 根据匿名状态生成描述
            if (Flags.HasFlag(StatusFlags.IsIdHidden))
            {
                desc = "[匿名]";
            }
            else if (Flags.HasFlag(StatusFlags.IsAnonymous))
            {
                if (UserConfigAnonymousFlags.HasFlag(StatusFlags.IsIdHidden))
                {
                    desc = $"{GetEntityDesc()} (试图匿名)";
                }
                else
                {
                    desc = "[匿名]";
                }
            }
            else
            {
                desc = $"{GetEntityDesc()}";
            }

            if (!Flags.HasFlag(StatusFlags.IsWaymarkEnabled))
                desc += " (未使用标点)";
            return desc;
        }
    }
}

